CREATE OR REPLACE PROCEDURE pls_valida_ocorrencia_financ ( dt_ref_p pls_evento_movimento.dt_movimento%type, nr_seq_periodo_p pls_evento_movimento.nr_seq_periodo%type, nr_seq_lote_p INOUT pls_lote_pagamento.nr_sequencia%type, ds_retorno_p INOUT pls_lote_pagamento.ie_status%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Verificar se já existem lotes de pagamentos cadastrados ou gerados para o mês
	competência da ocorrência cadastrada
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_lote_w		pls_lote_pagamento.nr_sequencia%type;
ie_status_w		pls_lote_pagamento.ie_status%type;
qt_prest_pgto_w		bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_lote_w
from	pls_lote_pagamento
where	((trunc(dt_mes_competencia) = trunc(dt_ref_p,'month')) or (trunc(dt_ref_p) between trunc(dt_inicio_comp) and fim_dia(dt_fim_comp)))
and	nr_seq_periodo 	= nr_seq_periodo_p;

	nr_seq_lote_p 		:= null;
	ds_retorno_p	 	:= null;

	if (coalesce(nr_seq_lote_w,0) > 0) then
		select	ie_status
		into STRICT	ie_status_w
		from	pls_lote_pagamento
		where	nr_sequencia = nr_seq_lote_w;

		if (ie_status_w = 'D') then
			ds_retorno_p 	:= 'F';
		else
			select	count(1)
			into STRICT	qt_prest_pgto_w
			from	pls_pagamento_prestador
			where	nr_seq_lote = nr_seq_lote_w;

			if (coalesce(qt_prest_pgto_w,0) > 0) then
				ds_retorno_p	:= 'G';
			end if;
		end if;
		nr_seq_lote_p	:= nr_seq_lote_w;
	end if;
--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
