nr_seq_controle_int_guia_w	pls_cad_controle_interno.nr_sequencia%type;
ie_obriga_controle_int_w	pls_validacao_aut_pro_int.ie_obriga_controle_int%type;
nr_seq_controle_interno_w	pls_validacao_aut_pro_int.nr_seq_controle_interno%type;
nr_seq_guia_proc_w		pls_guia_plano.nr_sequencia%type;
cd_procedimento_w		procedimento.cd_procedimento%type;
ie_origem_proced_w		procedimento.ie_origem_proced%type;
nr_seq_guia_mat_w		pls_guia_plano.nr_sequencia%type;
nr_seq_material_w		pls_material.nr_sequencia%type;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;
nr_seq_oc_benef_w		pls_ocorrencia_benef.nr_sequencia%type;
ie_gerar_ocorrencia_w		varchar(1);
ie_regra_w			varchar(1);
ie_tipo_ocorrencia_w		varchar(1);

C01 CURSOR FOR 
	SELECT	ie_obriga_controle_int, 
		nr_seq_controle_interno 
	from	pls_validacao_aut_pro_int 
	where	nr_seq_ocor_aut_combinada	= nr_seq_ocor_combinada_p 
	and	ie_situacao			= 'A';

C02 CURSOR FOR 
	SELECT	nr_sequencia, 
		cd_procedimento, 
		ie_origem_proced 
	from	pls_guia_plano_proc 
	where	nr_seq_guia		= nr_seq_guia_p;
	
C03 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_material 
	from	pls_guia_plano_mat 
	where	nr_seq_guia		= nr_seq_guia_p;

 

BEGIN 
 
begin 
	select	b.nr_seq_controle_interno, 
		a.nr_seq_prestador 
	into STRICT	nr_seq_controle_int_guia_w, 
		nr_seq_prestador_w 
	from	pls_guia_plano a, 
		pls_controle_interno_guia b 
	where	b.nr_seq_guia = a.nr_sequencia 
	and	a.nr_sequencia = nr_seq_guia_p;
exception 
when others then 
	nr_seq_controle_int_guia_w	:= null;
	nr_seq_prestador_w		:= null;
end;
 
open C01;
loop 
fetch C01 into	 
	ie_obriga_controle_int_w, 
	nr_seq_controle_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	ie_regra_w		:= 'S';
	ie_gerar_ocorrencia_w	:= 'N';
 
	if ( ie_obriga_controle_int_w = 'N' ) then 
		if ( nr_seq_controle_int_guia_w = nr_seq_controle_interno_w ) then 
			ie_gerar_ocorrencia_w := 'S';
		end if;
	elsif ( ie_obriga_controle_int_w = 'S' ) then 
		if ( nr_seq_controle_int_guia_w <> nr_seq_controle_interno_w ) or ( coalesce(nr_seq_controle_int_guia_w::text, '') = '' ) then 
			ie_gerar_ocorrencia_w := 'S';
		end if;
	end if;
 
	if ( ie_utiliza_filtro_p = 'S' ) and ( ie_gerar_ocorrencia_w = 'S' )then 
 
		ie_gerar_ocorrencia_w	:= 'N';
 
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_guia_proc_w, 
			cd_procedimento_w, 
			ie_origem_proced_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			/* Tratamento para filtros */
 
			SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, nr_seq_guia_p, null, null, nr_seq_guia_proc_w, null, null, null, null, cd_procedimento_w, ie_origem_proced_w, null, null, null, null, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
 
			if ( ie_regra_w	= 'S' ) then 
				ie_gerar_ocorrencia_w	:= 'S';
			elsif ( ie_regra_w	= 'E' ) then 
				ie_gerar_ocorrencia_w	:= 'N';
				exit;
			end if;
			end;
		end loop;
		close C02;
 
		open C03;
		loop 
		fetch C03 into 
			nr_seq_guia_mat_w, 
			nr_seq_material_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin 
			/* Tratamento para filtros */
 
			SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, nr_seq_guia_p, null, null, null, nr_seq_guia_mat_w, null, null, null, null, null, nr_seq_material_w, null, null, null, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
 
			if ( ie_regra_w	= 'S' ) then 
				ie_gerar_ocorrencia_w	:= 'S';
			elsif ( ie_regra_w	= 'E' ) then 
				ie_gerar_ocorrencia_w	:= 'N';					
				exit;
			end if;
			end;
		end loop;
		close C03;
 
		/* Tratamento para filtros */
 
		SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, nr_seq_guia_p, null, null, null, null, null, null, null, null, null, null, null, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
					 
		if ( ie_regra_w	= 'S' ) then 
			ie_gerar_ocorrencia_w	:= 'S';
		elsif ( ie_regra_w	= 'E' ) then 
			ie_gerar_ocorrencia_w	:= 'N';
		end if;
	end if;
		 
	if ( ie_gerar_ocorrencia_w	= 'S' ) then		 
		nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_p, nr_seq_ocorrencia_p, null, nr_seq_guia_p, null, null, null, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, 7, cd_estabelecimento_p, 'N', null, nr_seq_oc_benef_w, null, null, null, null);
				 
		CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p, 
						nr_seq_guia_p, null, null, 
						null, null, null, 
						null, null, null, 
						nm_usuario_p, cd_estabelecimento_p);
	end if;
	end;
end loop;
close C01;
 
