SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_valida_ocor_aut_estip_con ( nr_seq_ocor_combinada_p pls_ocor_aut_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_utiliza_filtro_p pls_ocor_aut_combinada.ie_utiliza_filtro%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar as regras de contrato para analise de estipulante/contratante
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_gerar_ocorrencia_w		varchar(2);
qt_existe_grupo_w		bigint;
nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;
ie_tipo_ocorrencia_w		varchar(2);
ie_regra_w			varchar(2);	
nr_seq_oc_benef_w		pls_ocorrencia_benef.nr_sequencia%type;	
ie_gerou_ocor_cabecalho_w	varchar(2);
nr_contrato_w			pls_contrato.nr_contrato%type;
nr_seq_intercambio_w		pls_intercambio.nr_sequencia%type;
ie_gera_analise_estip_ext_w	varchar(2)	:= 'N';

C01 CURSOR FOR
	SELECT	nr_contrato,
		nr_seq_intercambio,
		nr_seq_grupo_contrato
	from	pls_validacao_aut_est_con
	where	nr_seq_ocor_aut_combinada	= nr_seq_ocor_combinada_p
	and	ie_situacao			= 'A';

C02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

C03 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_material
	from	pls_requisicao_mat
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
	
BEGIN

for r_C01_w in C01 loop
	begin
		ie_gerar_ocorrencia_w	:= 'S';
		
		begin
			select	pls_obter_dados_contrato(nr_seq_contrato, 'N'),
				nr_seq_intercambio
			into STRICT	nr_contrato_w,
				nr_seq_intercambio_w
			from 	pls_segurado
			where	nr_sequencia	= nr_seq_segurado_p;
		exception
		when others then
			nr_contrato_w		:= null;
			nr_seq_intercambio_w	:= null;
		end;
					
		if (coalesce(r_C01_w.nr_contrato,0) <> 0) and (r_C01_w.nr_contrato <> coalesce(nr_contrato_w,0)) then	
			ie_gerar_ocorrencia_w	:= 'N';
		 
		elsif (coalesce(r_C01_w.nr_seq_intercambio,0) <> 0) and (r_C01_w.nr_seq_intercambio <> coalesce(nr_seq_intercambio_w,0)) then	 
			ie_gerar_ocorrencia_w	:= 'N';
			
		elsif (coalesce(r_C01_w.nr_seq_grupo_contrato,0) <> 0) then	
			select	count(1)
			into STRICT	qt_existe_grupo_w
			from	pls_preco_contrato	
			where	nr_seq_grupo		= r_C01_w.nr_seq_grupo_contrato
			and (nr_seq_contrato	= pls_obter_seq_contrato(nr_contrato_w)
			or	nr_seq_intercambio	= nr_seq_intercambio_w);	

			if (qt_existe_grupo_w = 0) then
				ie_gerar_ocorrencia_w	:= 'N';
			end if;
		end if;
		
		if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') and (ie_gerar_ocorrencia_w = 'S') then
			for r_C02_w in C02 loop
				begin
					if (ie_utiliza_filtro_p	= 'S') then
					/* Tratamento para filtros */

						SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, null, nr_seq_requisicao_p, null, null, null, r_C02_w.nr_sequencia, null, null, r_C02_w.cd_procedimento, r_C02_w.ie_origem_proced, null, null, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;

						if (ie_regra_w	= 'S') then
							ie_gerar_ocorrencia_w	:= 'S';
						elsif (ie_regra_w	in ('E','N')) then
							ie_gerar_ocorrencia_w	:= 'N';
						end if;							
					end if;
				end;
			end loop;
			
			for r_C03_w in C03 loop
				begin
					if (ie_utiliza_filtro_p	= 'S') then
					/* Tratamento para filtros */

						SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, null, nr_seq_requisicao_p, null, null, null, null, r_C03_w.nr_sequencia, null, null, null, r_C03_w.nr_seq_material, null, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;

						if (ie_regra_w	= 'S') then
							ie_gerar_ocorrencia_w	:= 'S';
						elsif (ie_regra_w	in ('E','N')) then
							ie_gerar_ocorrencia_w	:= 'N';
						end if;
					end if;
				end;
			end loop;
			
			if (ie_gerar_ocorrencia_w = 'S') then			
				ie_gera_analise_estip_ext_w := pls_gerar_analise_extern_estip(	nr_seq_ocorrencia_p, nr_seq_requisicao_p, r_C01_w.nr_contrato, r_C01_w.nr_seq_intercambio, r_C01_w.nr_seq_grupo_contrato, nm_usuario_p, ie_gera_analise_estip_ext_w);
				
				if (ie_gera_analise_estip_ext_w	= 'S') then							
					nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_p, nr_seq_ocorrencia_p, nr_seq_requisicao_p, null, null, null, null, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, 9, cd_estabelecimento_p, 'N', null, nr_seq_oc_benef_w, null, null, null, null);
							
					CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p,
									null, nr_seq_requisicao_p, null,
									null, null, null,
									null, null, null,
									nm_usuario_p, cd_estabelecimento_p);
				end if;
			end if;
		end if;
	end;
end loop;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_valida_ocor_aut_estip_con ( nr_seq_ocor_combinada_p pls_ocor_aut_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_utiliza_filtro_p pls_ocor_aut_combinada.ie_utiliza_filtro%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) FROM PUBLIC;

