CREATE OR REPLACE PROCEDURE pls_valida_ocor_aut_fornec_mat ( nr_seq_ocor_combinada_p bigint, nr_seq_ocorrencia_p bigint, nr_seq_segurado_p bigint, nr_seq_motivo_glosa_p bigint, nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_execucao_p bigint, ie_utiliza_filtro_p text, nr_seq_param1_p bigint, nr_seq_param2_p bigint, nr_seq_param3_p bigint, nr_seq_param4_p bigint, nr_seq_param5_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_gerar_ocorrencia_w		varchar(2)	:= 'N';
nr_seq_material_w		bigint;
nr_seq_oc_benef_w		bigint;
ie_gerou_ocor_cabecalho_w	varchar(2);
ie_tipo_item_w			bigint;
ie_tipo_ocorrencia_w	varchar(2);
ie_regra_w				varchar(2);
nr_seq_guia_mat_w		bigint;
ie_situacao_w			varchar(2);
ie_valida_fornec_inativo_w	varchar(1);
nr_seq_prest_fornec_w	bigint;
cd_material_opm_w		bigint;
				
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
			nr_seq_material, 
			nr_seq_prest_fornec 
	from	pls_guia_plano_mat 
	where	nr_seq_guia		= nr_seq_guia_p;
	

BEGIN 
	begin 
		select	ie_valida_fornec_inativo 
		into STRICT	ie_valida_fornec_inativo_w 
		from	pls_validacao_aut_forn_mat 
		where	nr_seq_ocor_aut_combinada 	= nr_seq_ocor_combinada_p 
		and		ie_situacao 	= 'A';
	exception 
	when others then 
		ie_valida_fornec_inativo_w	:= 'N';
	end;
	 
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') and (ie_valida_fornec_inativo_w = 'S') then 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_guia_mat_w, 
		nr_seq_material_w, 
		nr_seq_prest_fornec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		if (nr_seq_prest_fornec_w <> 0) then 
		ie_gerar_ocorrencia_w	:= 'N';
			begin 
				select	max(cd_material_ops) 
				into STRICT	cd_material_opm_w 
				from	pls_material 
				where	nr_sequencia	= nr_seq_material_w;
			exception 
			when others then 
				cd_material_opm_w	:= null;
			end;			
			 
			begin 
				select	b.ie_situacao 
				into STRICT	ie_situacao_w 
				from	pls_mat_unimed_fed_sc a, 
					pls_mat_uni_fed_sc_preco b, 
					pls_prestador c, 
					pls_fornec_mat_fed_sc d 
				where	a.cd_material 	= cd_material_opm_w 
				and	a.nr_sequencia	= b.nr_seq_mat_unimed 
				and 	b.cd_fornecedor_fed_sc = cd_fornecedor 
				and	d.cd_cgc	= c.cd_cgc 
				and	c.nr_sequencia	= nr_seq_prest_fornec_w 
				and 	b.dt_preco	= ( 	SELECT max(x.dt_preco) 
								from pls_mat_uni_fed_sc_preco x 
								where x.nr_seq_mat_unimed = b.nr_seq_mat_unimed 
								and 	x.cd_fornecedor_fed_sc = d.cd_fornecedor 
								and	x.ie_situacao = 'A');
			exception 
			when others then 
				ie_situacao_w	:= 'I';
			end;		
 
			if (ie_situacao_w 	= 'I') then 
				ie_gerar_ocorrencia_w	 := 'S';
			end if;
				 
			if (ie_gerar_ocorrencia_w	= 'S') and (ie_utiliza_filtro_p = 'S') then 
				ie_gerar_ocorrencia_w	:= 'N';
				/* Tratamento para filtros */
 
				SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, nr_seq_guia_p, null, null, null, nr_seq_guia_mat_w, null, null, null, null, null, nr_seq_material_w, ie_gerou_ocor_cabecalho_w, null, null, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
 
				if (ie_regra_w	= 'S') then 
					ie_gerar_ocorrencia_w	:= 'S';
				elsif (ie_regra_w	in ('E','N')) then 
					ie_gerar_ocorrencia_w	:= 'N';
				end if;
			end if;
	 
			if (ie_gerar_ocorrencia_w	= 'S')	then 
				if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
					ie_tipo_item_w	:= 2;	
				end if;
			 
			nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_p, nr_seq_ocorrencia_p, null, nr_seq_guia_p, null, null, nr_seq_guia_mat_w, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, ie_tipo_item_w, cd_estabelecimento_p, 'N', null, nr_seq_oc_benef_w, null, null, null, null);
 
			CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p, 
							nr_seq_guia_p, nr_seq_requisicao_p, nr_seq_execucao_p, 
							null, nr_seq_guia_mat_w, null, 
							null, null, null, 
							nm_usuario_p, cd_estabelecimento_p);						
 
			end if;
		end if;
	end;
	end loop;
	close C01;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
