SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_valida_ocor_aut_proc_pct ( nr_seq_ocor_combinada_p pls_ocor_aut_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_execucao_p pls_execucao_requisicao.nr_sequencia%type, ie_utiliza_filtro_p pls_ocor_aut_combinada.ie_utiliza_filtro%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ x ] Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
--qt_regra_valid_aut_cpt_cid_w	number(10); 
ie_gerar_ocorrencia_w		varchar(2);
nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;	
ie_tipo_ocorrencia_w		varchar(2);
ie_regra_w			varchar(2);	
nr_seq_oc_benef_w		pls_ocorrencia_benef.nr_sequencia%type;	
ie_gerou_ocor_cabecalho_w	varchar(2);

C01 CURSOR FOR 
	SELECT	ie_situacao 
	from	pls_validacao_aut_proc_pct 
	where	nr_seq_ocor_aut_combinada	= nr_seq_ocor_combinada_p 
	and	ie_situacao			= 'A';

C02 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		b.cd_procedimento, 
		b.ie_origem_proced 
	from	pls_guia_plano_proc 	b, 
		pls_guia_plano		a 
	where	a.nr_sequencia	= nr_seq_guia_p 
	and	b.nr_seq_guia	= a.nr_sequencia;

 
C03 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		b.cd_procedimento, 
		b.ie_origem_proced 
	from	pls_requisicao_proc 	b, 
		pls_requisicao		a 
	where	a.nr_sequencia 		= nr_seq_requisicao_p 
	and	b.nr_seq_requisicao	= a.nr_sequencia;

C04 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		b.cd_procedimento, 
		b.ie_origem_proced 
	from	pls_execucao_req_item	b, 
		pls_requisicao		a 
	where	nr_seq_execucao		= nr_seq_execucao_p 
	and	a.nr_sequencia		= b.nr_seq_requisicao 
	and	coalesce(nr_seq_material::text, '') = '';

BEGIN 
for r_C01_w in C01 loop 
	begin		 
		if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
			begin 
				select	nr_seq_prestador, 
					nr_seq_segurado 
				into STRICT	nr_seq_prestador_w, 
					nr_seq_segurado_w 
				from	pls_guia_plano 
				where 	nr_sequencia	= nr_seq_guia_p;
			exception 
			when others then 
				nr_seq_prestador_w	:= null;
			end;	
		 
			for r_C02_w in C02 loop 
				begin 
					ie_gerar_ocorrencia_w	:= 'S';	
															 
					if (ie_utiliza_filtro_p	= 'S') then 
						/* Tratamento para filtros */
 
						SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, nr_seq_guia_p, nr_seq_requisicao_p, nr_seq_execucao_p, r_C02_w.nr_sequencia, null, null, null, null, r_C02_w.cd_procedimento, r_C02_w.ie_origem_proced, null, ie_gerou_ocor_cabecalho_w, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
										 
						if (ie_regra_w	= 'S') then 
							ie_gerar_ocorrencia_w	:= 'S';
						elsif (ie_regra_w	in ('E','N')) then 
							ie_gerar_ocorrencia_w	:= 'N';
						end if;
					end if;		
					 
					if (ie_gerar_ocorrencia_w	= 'S') then 
						if (coalesce(r_C01_w.ie_situacao,'I') = 'A') then 
							 
							ie_gerar_ocorrencia_w	:= pls_verifica_se_exige_proc_lib(nr_seq_guia_p, null, null);
						end if;
						if (ie_gerar_ocorrencia_w	= 'S') then							 
							nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_w, nr_seq_ocorrencia_p, null, nr_seq_guia_p, null, r_C02_w.nr_sequencia, null, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, 1, cd_estabelecimento_p, 'N', null, nr_seq_oc_benef_w, null, null, null, null);
									 
							CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p, 
											nr_seq_guia_p, null, null, 
											r_C02_w.nr_sequencia, null, null, 
											null, null, null, 
											nm_usuario_p, cd_estabelecimento_p);
						end if;
					end if;
				end;
			end loop;
		elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then	 
			begin 
				select	nr_seq_prestador, 
					nr_seq_segurado 
				into STRICT	nr_seq_prestador_w, 
					nr_seq_segurado_w 
				from	pls_requisicao 
				where 	nr_sequencia	= nr_seq_requisicao_p;
			exception 
			when others then 
				nr_seq_prestador_w	:= null;
			end;		
		 
			for r_C03_w in C03 loop 
				begin 
					ie_gerar_ocorrencia_w	:= 'S';	
							 
					if (ie_utiliza_filtro_p	= 'S') then 
						/* Tratamento para filtros */
 
						SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, null, nr_seq_requisicao_p, null, null, null, r_C03_w.nr_sequencia, null, null, r_C03_w.cd_procedimento, r_C03_w.ie_origem_proced, null, null, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
										 
						if (ie_regra_w	= 'S') then 
							ie_gerar_ocorrencia_w	:= 'S';
						elsif (ie_regra_w	in ('E','N')) then 
							ie_gerar_ocorrencia_w	:= 'N';
						end if;							
					end if;
				 
					if (ie_gerar_ocorrencia_w	= 'S') then 
					 
						if (coalesce(r_C01_w.ie_situacao,'I') = 'A') then 
							ie_gerar_ocorrencia_w	:= pls_verifica_se_exige_proc_lib(null, nr_seq_requisicao_p, null);
						end if;
						 
						if (ie_gerar_ocorrencia_w	= 'S') then							 
							nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_w, nr_seq_ocorrencia_p, nr_seq_requisicao_p, null, null, r_C03_w.nr_sequencia, null, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, 5, cd_estabelecimento_p, 'N', null, nr_seq_oc_benef_w, null, null, null, null);
									 
							CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p, 
											null, nr_seq_requisicao_p, null, 
											null, null, r_C03_w.nr_sequencia, 
											null, null, null, 
											nm_usuario_p, cd_estabelecimento_p);
						end if;
					end if;
				end;
			end loop;
		elsif (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then	 
			begin 
				select	a.nr_seq_requisicao, 
					b.nr_seq_prestador, 
					b.nr_seq_segurado 
				into STRICT	nr_seq_requisicao_w, 
					nr_seq_prestador_w, 
					nr_seq_segurado_w 
				from	pls_execucao_requisicao a, 
					pls_requisicao b 
				where 	a.nr_seq_requisicao	= b.nr_sequencia 
				and	a.nr_sequencia		= nr_seq_execucao_p;
			exception 
			when others then 
				nr_seq_prestador_w	:= null;
			end;	
 
			for r_C04_w in C04 loop 
				begin 
					ie_gerar_ocorrencia_w	:= 'S';
 
					if (ie_utiliza_filtro_p	= 'S') then 
						/* Tratamento para filtros */
 
						SELECT * FROM pls_gerar_ocor_aut_filtro(	nr_seq_ocor_combinada_p, null, null, nr_seq_execucao_p, null, null, null, null, r_C04_w.nr_sequencia, r_C04_w.cd_procedimento, r_C04_w.ie_origem_proced, null, ie_gerou_ocor_cabecalho_w, nr_seq_prestador_w, nr_seq_ocorrencia_p, null, null, null, nm_usuario_p, ie_regra_w, ie_tipo_ocorrencia_w) INTO STRICT ie_regra_w, ie_tipo_ocorrencia_w;
										 
						if (ie_regra_w	= 'S') then 
							ie_gerar_ocorrencia_w	:= 'S';
						elsif (ie_regra_w	in ('E','N')) then 
							ie_gerar_ocorrencia_w	:= 'N';
						end if;
					end if;
					 
					if (ie_gerar_ocorrencia_w	= 'S') then 
					 
						if (coalesce(r_C01_w.ie_situacao,'I') = 'A') then			 
							ie_gerar_ocorrencia_w	:= pls_verifica_se_exige_proc_lib(null, null, nr_seq_execucao_p);
						end if;
						 
						if (ie_gerar_ocorrencia_w	= 'S') then						 
							nr_seq_oc_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_w, nr_seq_ocorrencia_p, null, null, null, r_C04_w.nr_sequencia, null, nr_seq_ocor_combinada_p, nm_usuario_p, null, nr_seq_motivo_glosa_p, 10, cd_estabelecimento_p, 'N', nr_seq_execucao_p, nr_seq_oc_benef_w, null, null, null, null);
								 
							CALL pls_atualizar_status_ocor_comb(	nr_seq_ocorrencia_p, nr_seq_ocor_combinada_p, nr_seq_motivo_glosa_p, 
											null, null, nr_seq_execucao_p, 
											null, null, null, 
											null, r_C04_w.nr_sequencia, null, 
											nm_usuario_p, cd_estabelecimento_p);
						end if;
					end if;
				end;
			end loop;
		end if;
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_valida_ocor_aut_proc_pct ( nr_seq_ocor_combinada_p pls_ocor_aut_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_execucao_p pls_execucao_requisicao.nr_sequencia%type, ie_utiliza_filtro_p pls_ocor_aut_combinada.ie_utiliza_filtro%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) FROM PUBLIC;

