CREATE OR REPLACE PROCEDURE pls_valida_tabela_preco ( nr_seq_tabela_p pls_tabela_preco.nr_sequencia%type, nr_seq_tabela_origem_p pls_tabela_preco.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_contrato_tabela_p pls_tabela_preco.nr_contrato%type, ie_tipo_contratacao_p text, ie_permite_tab_dif_p text, ie_consiste_tab_contr_p text) AS $body$
DECLARE


qt_registro_w		bigint;
nr_seg_vin_tabela_w	bigint;


BEGIN

if	(ie_permite_tab_dif_p = 'N' AND ie_permite_tab_dif_p IS NOT NULL AND ie_permite_tab_dif_p::text <> '') then
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_contrato_plano a
	where	a.nr_seq_contrato	= nr_seq_contrato_p
	and	a.nr_seq_tabela		= nr_seq_tabela_p;

	if (qt_registro_w = 0) then
		select	max(nr_segurado)
		into STRICT	nr_seg_vin_tabela_w
		from	pls_tabela_preco
		where	nr_sequencia = nr_seq_tabela_p;

		select	count(1)
		into STRICT	qt_registro_w
		from	pls_contrato_plano a
		where	a.nr_seq_contrato	= nr_seq_contrato_p
		and	a.nr_seq_tabela		= nr_seq_tabela_origem_p;

		if	((coalesce(nr_seg_vin_tabela_w::text, '') = '') or
			(nr_seg_vin_tabela_w IS NOT NULL AND nr_seg_vin_tabela_w::text <> '' AND qt_registro_w = 0)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort( 189022,'NR_SEQ_SEGURADO='||nr_seq_segurado_p||';'||'NM_SEGURADO='||pls_obter_nome_beneficiario(nr_seq_segurado_p));
		end if;
	end if;
end if;

if	((ie_consiste_tab_contr_p = 'S' AND ie_consiste_tab_contr_p IS NOT NULL AND ie_consiste_tab_contr_p::text <> '') and (nr_seq_contrato_p <> nr_seq_contrato_tabela_p) and (ie_tipo_contratacao_p <> 'I')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 189008,'NR_SEQ_SEGURADO='||nr_seq_segurado_p||';'||'NM_SEGURADO='||pls_obter_nome_beneficiario(nr_seq_segurado_p));
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
