SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_valida_valor_demonstrativo ( nr_seq_protocolo_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


vl_demonstrativo_w	double precision;
vl_protocolo_w		double precision;


BEGIN

/* Obter o valor dos demonstrativos do protocolo */

select	coalesce(sum(vl_titulo),0)
into STRICT	vl_demonstrativo_w
from	pls_prot_conta_titulo
where	nr_seq_protocolo	= nr_seq_protocolo_p;

/* Obter o valor do protocolo */

select	coalesce(vl_total,0)
into STRICT	vl_protocolo_w
from	pls_protocolo_conta
where	nr_sequencia	= nr_seq_protocolo_p;

if (vl_demonstrativo_w > 0) and (vl_demonstrativo_w <> vl_protocolo_w) then
	ds_retorno_p	:= 'Valor do protocolo (R$ ' || vl_protocolo_w || ') diferente do valor total dos demonstrativos (R$ ' || vl_demonstrativo_w || ')';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_valida_valor_demonstrativo ( nr_seq_protocolo_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

