CREATE OR REPLACE PROCEDURE pls_valorizar_proc_conta ( nr_seq_proc_conta_p pls_processo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

 
ds_irrelevante_w	varchar(4000);
dt_competencia_w	timestamp;
vl_total_hospitalar_w	pls_processo_procedimento.vl_proc_calculado%type;
				
C01 CURSOR(nr_seq_conta_cp	pls_processo_conta.nr_sequencia%type) FOR 
SELECT	nr_sequencia, 
	cd_procedimento, 
	ie_origem_proced, 
	qt_procedimento 
from	pls_processo_procedimento 
where	nr_seq_conta		= nr_seq_conta_cp;

BEGIN 
 
select	max(dt_competencia) 
into STRICT	dt_competencia_w 
from	pls_processo_conta 
where	nr_sequencia	= nr_seq_proc_conta_p;
 
for r_c01_w in c01(nr_seq_proc_conta_p) loop 
 
	SELECT * FROM sus_define_preco_proced(dt_competencia_w, r_c01_w.cd_procedimento, r_c01_w.ie_origem_proced, cd_estabelecimento_p, 1, 	-- Internação 
			ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, vl_total_hospitalar_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w) INTO STRICT 
			ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, vl_total_hospitalar_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w, ds_irrelevante_w;
	 
	update	pls_processo_procedimento 
	set	vl_proc_calculado	= (r_c01_w.qt_procedimento * vl_total_hospitalar_w) 
	where	nr_sequencia		= r_c01_w.nr_sequencia;
 
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
