SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_val_guia_med_cooperado ( ie_insere_p INOUT text, ie_ausente_p INOUT text, ie_opcao_p text, ie_filtro_ausentes_p text, cd_cgc_p text, cd_pessoa_fisica_p text, nr_seq_cooperado_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: VALIDAR TODAS AS CONDIÇÕES DE COOPERADO
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	UTILIZAR O IE_OPCAO_P
	"A" - Validação de ausencia

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_ausencia_w		integer := 0;
qt_ausencia_guia_w	integer := 0;


BEGIN
if (ie_opcao_p = 'A') then
	ie_ausente_p	:= 'N';
	ie_insere_p	:= 'S';

	if (coalesce(cd_cgc_p::text, '') = '') then
		select	count(1)
		into STRICT	qt_ausencia_w
		
		where	exists (SELECT	1
				from	pls_cooperado_ausencia y,
					pls_cooperado x
				where	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
				and	x.nr_sequencia 		= y.nr_seq_cooperado
				and	clock_timestamp() between y.dt_inicio and coalesce(y.dt_fim,clock_timestamp()));

		-- VERIFICA SE TEM AUSENCIA E APRESENTA NO GUIA MÉDICO
		select	count(1)
		into STRICT	qt_ausencia_guia_w
		
		where	exists (SELECT	1
				from	pls_cooperado_ausencia y,
					pls_cooperado x
				where	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
				and	x.nr_sequencia 		= y.nr_seq_cooperado
				and	clock_timestamp() between y.dt_inicio and coalesce(y.dt_fim,clock_timestamp())
				and	coalesce(y.ie_guia_medico,'S') = 'N');

	elsif (coalesce(cd_pessoa_fisica_p::text, '') = '') then
		select	count(1)
		into STRICT	qt_ausencia_w
		
		where	exists (SELECT	1
				from	pls_cooperado_ausencia y,
					pls_cooperado x
				where	x.cd_cgc 	= cd_cgc_p
				and	x.nr_sequencia 	= y.nr_seq_cooperado
				and	clock_timestamp() between y.dt_inicio and coalesce(y.dt_fim,clock_timestamp()));

		-- VERIFICA SE TEM AUSENCIA E APRESENTA NO GUIA MÉDICO
		select	count(1)
		into STRICT	qt_ausencia_guia_w
		
		where	exists (SELECT	1
				from	pls_cooperado_ausencia y,
					pls_cooperado x
				where	x.cd_cgc 	= cd_cgc_p
				and	x.nr_sequencia 	= y.nr_seq_cooperado
				and	clock_timestamp() between y.dt_inicio and coalesce(y.dt_fim,clock_timestamp())
				and	coalesce(y.ie_guia_medico,'S') = 'N');
	end if;

	-- UTILIZADO PARA VERIFICAR COM O PARÂMETRO DE AUSENCIA - SE DEVE APARECER NO GUIA MÉDICO OU NÃO
	if (coalesce(qt_ausencia_guia_w, 0) > 0) then
		ie_ausente_p := 'S';
	else
		ie_ausente_p := 'N';
	end if;

	if (ie_filtro_ausentes_p = 'S') and (qt_ausencia_w > 0) then
		ie_ausente_p := 'S';
	end if;

	-- Verificar se deve apresentar conforme o parâmetro
	if (ie_filtro_ausentes_p <> 'A') then
		if (ie_ausente_p <> ie_filtro_ausentes_p) then
			ie_insere_p := 'N';
		end if;
	end if;

	-- Preenche o campo de ausência do cooperado da tabela w_pls_guia_medico
	if (coalesce(qt_ausencia_w, 0) > 0) then
		ie_ausente_p := 'S';
	else
		ie_ausente_p := 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_val_guia_med_cooperado ( ie_insere_p INOUT text, ie_ausente_p INOUT text, ie_opcao_p text, ie_filtro_ausentes_p text, cd_cgc_p text, cd_pessoa_fisica_p text, nr_seq_cooperado_p bigint) FROM PUBLIC;

