SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_val_imp_a1200_comp_anexo ( nr_seq_anexo_p pls_pacote_comp_anexo.nr_sequencia%type, nr_seq_composicao_p pls_pacote_composicao.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_regra_p pls_pacote_tipo_acomodacao.nr_sequencia%type, dt_vigencia_envio_p pls_pacote_comp_anexo.dt_vigencia_envio%type, ie_tipo_anexo_p pls_pacote_comp_anexo.ie_tipo_anexo%type) AS $body$
DECLARE


qt_registro_w	integer;
nr_seq_anexo_w	pls_pacote_comp_anexo.nr_sequencia%type;	

BEGIN

nr_seq_anexo_w := coalesce(nr_seq_anexo_p,-1);
qt_registro_w := 0;

-- valida o prestador
if (qt_registro_w = 0) and (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') and (ie_tipo_anexo_p = '4') then

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_pacote_comp_anexo		a
	where	a.nr_seq_pacote_composicao	= nr_seq_composicao_p
	and	a.nr_seq_prestador		= nr_seq_prestador_p
	and	a.ie_situacao			= 'S'
	and	a.ie_tipo_anexo			= '4'
	and	a.nr_sequencia			!= nr_seq_anexo_w
	and	a.dt_vigencia_envio		between trunc(dt_vigencia_envio_p, 'MONTH') and fim_dia(last_day(dt_vigencia_envio_p));

	
end if;


-- valida a regra
if (qt_registro_w = 0) and (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') and (ie_tipo_anexo_p = '4') then

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_pacote_comp_anexo		a
	where	a.nr_seq_pacote_composicao	= nr_seq_composicao_p
	and	a.nr_seq_tipo_acomodacao	= nr_seq_regra_p
	and	a.ie_situacao			= 'S'
	and	a.ie_tipo_anexo			= '4'
	and	a.nr_sequencia			!= nr_seq_anexo_w
	and	a.dt_vigencia_envio		between trunc(dt_vigencia_envio_p, 'MONTH') and fim_dia(last_day(dt_vigencia_envio_p));

end if;


if (qt_registro_w = 0) and (coalesce(nr_seq_regra_p::text, '') = '') and (coalesce(nr_seq_prestador_p::text, '') = '') and (ie_tipo_anexo_p = '4') then

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_pacote_comp_anexo		a
	where	a.nr_seq_pacote_composicao	= nr_seq_composicao_p
	and	a.ie_situacao			= 'S'
	and	a.nr_sequencia			!= nr_seq_anexo_w
	and	a.ie_tipo_anexo			= '4'
	and	coalesce(a.nr_seq_tipo_acomodacao::text, '') = ''
	and	coalesce(a.nr_seq_prestador::text, '') = ''
	and	a.dt_vigencia_envio		between trunc(dt_vigencia_envio_p, 'MONTH') and fim_dia(last_day(dt_vigencia_envio_p));

end if;



if (qt_registro_w > 0) then

	CALL wheb_mensagem_pck.exibir_mensagem_abort(1124282);
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
