CREATE OR REPLACE PROCEDURE pls_verificar_eventos_atend ( nr_seq_atendimento_p pls_atendimento.nr_sequencia%type, nr_seq_evento_atend_p pls_atendimento_evento.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Realizar a validação e o update do último evento do atendimento que esteja com o estágio "Em atendimento"
quando for criado um novo evento.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [   ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_evento_atend_w		pls_atendimento_evento.nr_sequencia%type;
qt_pendencia_w			integer;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_evento_atend_w
from	pls_atendimento_evento
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	nr_sequencia		<> nr_seq_evento_atend_p
and	ie_status		= 'A';

if (nr_seq_evento_atend_w IS NOT NULL AND nr_seq_evento_atend_w::text <> '') then

	select	count(1)
	into STRICT	qt_pendencia_w
	from	pls_atend_evt_pend_resp
	where	nr_seq_atend_evento	= nr_seq_evento_atend_w
	and	coalesce(dt_solucao::text, '') = '';

	if (qt_pendencia_w > 0) then
		update	pls_atendimento_evento
		set	ie_status	= 'P',
			ds_observacao	= ds_observacao|| ' Evento alterado para Pendente pelo sistema.',
			dt_fim_evento 	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_evento_atend_w;
	else
		update	pls_atendimento_evento
		set	ie_status	= 'C',
			ds_observacao	= ds_observacao|| ' Evento concluído pelo sistema.',
			dt_fim_evento 	= clock_timestamp(),
			dt_conclusao	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_evento_atend_w;
	end if;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
