CREATE OR REPLACE PROCEDURE pls_verificar_lib_lote_mens ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type) AS $body$
DECLARE


qt_mens_pendente_liberacao_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_mens_pendente_liberacao_w
from	pls_mensalidade_liberacao
where	nr_seq_lote = nr_seq_lote_p
and	coalesce(dt_liberacao::text, '') = '';

if (qt_mens_pendente_liberacao_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1133771);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
