CREATE OR REPLACE PROCEDURE pls_verifica_analise_encerrada (nr_seq_conta_p pls_conta.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE



nr_seq_analise_w	pls_conta.nr_seq_analise%type;
qt_analise_aberta_p	integer;

BEGIN

select	max(nr_seq_analise)
into STRICT	nr_seq_analise_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

if (nr_seq_analise_w IS NOT NULL AND nr_seq_analise_w::text <> '') then
	select	count(1)
	into STRICT	qt_analise_aberta_p
	from	pls_analise_conta
	where	nr_sequencia	= nr_seq_analise_w
	and	ie_status	not in ('T','C')
	and	ie_auditoria	= 'S';

	if (qt_analise_aberta_p	> 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(390799);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
