CREATE OR REPLACE PROCEDURE pls_verifica_cart_ant_sib ( nr_seq_lote_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_retorno_sib_w		bigint;
cd_usuario_plano_w		varchar(30);
cd_usuario_plano_ww		varchar(30);	-- Sem os zeros a esquerda
C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_usuario_plano
	from	pls_retorno_sib a
	where	a.nr_seq_lote_sib	= nr_seq_lote_sib_p
	and	coalesce(a.nr_seq_segurado::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_retorno_sib_w,
	cd_usuario_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	pls_elimina_zeros_esquerda(cd_usuario_plano_w)
	into STRICT	cd_usuario_plano_ww
	;

	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado_cart_ant
	where	cd_usuario_ant	= cd_usuario_plano_w
	or	cd_usuario_ant	= cd_usuario_plano_ww;

	if (coalesce(nr_seq_segurado_w,0) > 0) then
		update	pls_retorno_sib
		set	nr_seq_segurado_vinculado	= nr_seq_segurado_w,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_retorno_sib_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
