CREATE OR REPLACE PROCEDURE pls_verifica_regra_senha_estip ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_segurado_p bigint, nr_contrato_p bigint, nm_usuario_p text, ie_exige_regra_p INOUT text) AS $body$
DECLARE


nr_seq_reg_senha_estp_w		bigint;
cd_senha_externa_w		varchar(50);
ie_exige_senha_ext_w		varchar(1);


BEGIN

nr_seq_reg_senha_estp_w	:= pls_obter_regra_senha_estip(	nr_seq_guia_p, nr_seq_requisicao_p, nr_seq_segurado_p,
							nr_contrato_p, nm_usuario_p);

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	coalesce(cd_senha_externa,'X')
	into STRICT	cd_senha_externa_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	coalesce(cd_senha_externa,'X')
	into STRICT	cd_senha_externa_w
	from	pls_requisicao
	where	nr_sequencia	= nr_seq_requisicao_p;
end if;

if (nr_seq_reg_senha_estp_w	= 0) then
	ie_exige_regra_p	:= 'N';
	ie_exige_senha_ext_w	:= 'N';
elsif (nr_seq_reg_senha_estp_w	<> 0) and (cd_senha_externa_w	<> 'X') then
	ie_exige_regra_p	:= 'N';
	ie_exige_senha_ext_w	:= 'N';
elsif (nr_seq_reg_senha_estp_w	<> 0) and (cd_senha_externa_w	= 'X') then
	ie_exige_regra_p	:= 'S';
	ie_exige_senha_ext_w	:= 'S';
end if;

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	update	pls_requisicao
	set	ie_exige_senha_ext	= ie_exige_senha_ext_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_requisicao_p;
end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
