SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_verifica_req_item_portal ( nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar requisições do portal que não possuem itens e cancelar as mesmas.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [X] Outros: JOB
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
ds_texto_hist_w			varchar(1000);

C00 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_requisicao a
	where	a.ie_tipo_processo	= 'P'
	and (a.ie_origem_solic	= 'P'
	or	coalesce(a.ie_origem_solic::text, '') = '')
	and	a.ie_estagio <> 3
	and	not exists ( 	SELECT	1
				from	pls_requisicao_proc x
				where   x.nr_seq_requisicao = a.nr_sequencia)
	and	not exists ( 	select	1
				from	pls_requisicao_mat y
				where   y.nr_seq_requisicao = a.nr_sequencia)
	and	a.dt_requisicao < trunc(clock_timestamp());


BEGIN

open C00;
loop
fetch C00 into
	nr_seq_requisicao_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin
		update	pls_requisicao
		set	ie_status		= 'C',
			ie_estagio		= 3,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_requisicao_w;

		ds_texto_hist_w := 'A requisição '|| nr_seq_requisicao_w || ' foi cancelada pela JOB: PLS_VERIFICA_REQ_ITEM_PORTAL por não possuir nenhum item informado.';
		CALL pls_requisicao_gravar_hist(nr_seq_requisicao_w,'L',ds_texto_hist_w,null,nm_usuario_p);

		commit;
	end;
end loop;
close C00;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_verifica_req_item_portal ( nm_usuario_p text) FROM PUBLIC;

