CREATE OR REPLACE PROCEDURE pls_verifica_se_carencia_rede ( nr_seq_prestador_p bigint, nr_seq_carencia_p bigint, ie_retorno_p INOUT text) AS $body$
DECLARE


ie_retorno_w		varchar(10);
qt_registros_w		bigint;
nr_seq_rede_w		bigint;
nr_seq_tipo_carencia_w	bigint;
ie_alterar_rede_w	varchar(10);

C01 CURSOR FOR
	SELECT	nr_seq_rede_atend,
		coalesce(ie_alterar_rede,'N')
	from	pls_carencia_rede
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_w;


BEGIN

ie_retorno_w	:= 'N';

select	count(*)
into STRICT	qt_registros_w
from	pls_carencia
where	nr_sequencia	= nr_seq_carencia_p
and	(nr_seq_regra_aut IS NOT NULL AND nr_seq_regra_aut::text <> '');

if (qt_registros_w > 0) then
	select	max(nr_seq_tipo_carencia)
	into STRICT	nr_seq_tipo_carencia_w
	from	pls_carencia
	where	nr_sequencia	= nr_seq_carencia_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_rede_w,
		ie_alterar_rede_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		/*aaschlote 02/01/2012 - OS 346352*/

		if (ie_alterar_rede_w = 'S') then
			ie_retorno_w	:= 'S';
			exit;
		end if;

		ie_retorno_w	:= pls_obter_se_prest_rede_atend(nr_seq_prestador_p,nr_seq_rede_w);

		if (ie_retorno_w = 'S') then
			exit;
		end if;

		end;
	end loop;
	close C01;
end if;

ie_retorno_p	:= ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
