CREATE OR REPLACE PROCEDURE pls_verif_natureza_ocorrencia (nr_seq_ocorrencia_p pls_evento.nr_sequencia%type) AS $body$
DECLARE


nr_seq_evento_w		pls_evento.nr_sequencia%type;
ie_natureza_w		pls_evento.ie_natureza%type;
vl_movimento_w		pls_evento_movimento.vl_movimento%type;
ie_cancelamento_w	pls_evento_movimento.ie_cancelamento%type;


BEGIN

select  nr_seq_evento,
	vl_movimento,
	ie_cancelamento
into STRICT  	nr_seq_evento_w,
	vl_movimento_w,
	ie_cancelamento_w
from   	pls_evento_movimento
where  	nr_sequencia = nr_seq_ocorrencia_p;

if (coalesce(ie_cancelamento_w::text, '') = '') then
	select  ie_natureza
	into STRICT  	ie_natureza_w
	from  	pls_evento
	where  	nr_sequencia = nr_seq_evento_w;

	if ( vl_movimento_w >= 0 ) then
		if ( ie_natureza_w = 'D' ) then
			update  pls_evento_movimento
			set  	vl_movimento = ( vl_movimento_w * -1 )
			where  	nr_sequencia = nr_seq_ocorrencia_p;
		end if;
	else
		if ( ie_natureza_w = 'P' ) then
			update  pls_evento_movimento
			set  	vl_movimento = ( vl_movimento_w * -1 )
			where  	nr_sequencia = nr_seq_ocorrencia_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
