SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_verif_reg_retorno_aud_orig ( nr_seq_requisicao_p bigint, nm_usuario_p text, nr_seq_regra_retorno_p INOUT bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar os itens da guia conforme a geração de ocorr~encia combinada.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_regra_w				varchar(255);
ie_tipo_item_w				varchar(2);
ie_status_w				varchar(2);
nr_seq_regra_ret_orig_w			bigint	:= 0;
nr_seq_req_proc_w			bigint;
nr_seq_grupo_w				bigint;
nr_seq_auditoria_w			bigint;
nr_seq_ordem_w				bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_status		= 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_req_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_regra_ret_orig_w	:= pls_obter_reg_retorno_aud_orig(nr_seq_req_proc_w, null, null);

	if (nr_seq_regra_ret_orig_w	> 0) then
		update	pls_auditoria_item
		set	ie_status		= 'A',
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_proc_origem	= nr_seq_req_proc_w;

		update	pls_requisicao_proc
		set	ie_status		= 'A',
			qt_procedimento		= 0,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_req_proc_w;
	end if;
	end;
end loop;
close C01;

if (nr_seq_regra_ret_orig_w	> 0) then
	begin
		select	nr_seq_grupo_auditor,
			substr(ds_regra,1,255)
		into STRICT	nr_seq_grupo_w,
			ds_regra_w
		from	pls_retorno_aud_origem
		where	nr_sequencia	= nr_seq_regra_ret_orig_w;
	exception
	when others then
		nr_seq_grupo_w	:= null;
		ds_regra_w	:= null;
	end;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_auditoria_w
		from	pls_auditoria
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		and	coalesce(dt_liberacao::text, '') = '';

		if (nr_seq_auditoria_w IS NOT NULL AND nr_seq_auditoria_w::text <> '') then
			update	pls_auditoria_grupo
			set	ie_status		= 'P',
				dt_liberacao		= clock_timestamp(),
				dt_atualizacao		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	nr_seq_auditoria	= nr_seq_auditoria_w
			and	coalesce(dt_liberacao::text, '') = '';

			select	coalesce(max(nr_seq_ordem),0) + 1
			into STRICT	nr_seq_ordem_w
			from	pls_auditoria_grupo
			where	nr_seq_auditoria	= nr_seq_auditoria_w;

			insert into pls_auditoria_grupo(nr_sequencia, nr_seq_auditoria, nr_seq_grupo,
				dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
				nm_usuario_nrec, nr_seq_ordem, ie_status,
				ie_manual)
			values (nextval('pls_auditoria_grupo_seq'), nr_seq_auditoria_w, nr_seq_grupo_w,
				clock_timestamp(), nm_usuario_p, clock_timestamp(),
				nm_usuario_p, nr_seq_ordem_w, 'U',
				'N');
		end if;
	end if;

	CALL pls_requisicao_gravar_hist(nr_seq_requisicao_p,'L',substr('Análise não finalizada devido a existência de regra de conversão na função OPS - Controle dos Grupos de Análise > Regras > Autorização > Regra retorno > Regra retorno auditoria origem beneficário > '||ds_regra_w,1,4000),null,nm_usuario_p);
end if;

nr_seq_regra_retorno_p	:= nr_seq_regra_ret_orig_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_verif_reg_retorno_aud_orig ( nr_seq_requisicao_p bigint, nm_usuario_p text, nr_seq_regra_retorno_p INOUT bigint) FROM PUBLIC;

