CREATE OR REPLACE PROCEDURE pls_vincular_beneficiario_inc ( nr_seq_inclusao_benef_p pls_inclusao_beneficiario.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_plano_w		pls_plano.nr_sequencia%type;
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

select	max(nr_seq_plano),
	max(nr_seq_contrato),
	max(cd_pessoa_fisica)
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w,
	cd_pessoa_fisica_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

update	pls_inclusao_beneficiario
set	nr_seq_segurado	= nr_seq_segurado_p,
	nr_seq_plano	= coalesce(nr_seq_plano,nr_seq_plano_w),
	nr_seq_contrato	= coalesce(nr_seq_contrato,nr_seq_contrato_w)
where	nr_sequencia	= nr_seq_inclusao_benef_p;

CALL pls_gravar_alt_solic_alteracao(nr_seq_inclusao_benef_p,nm_usuario_p);

CALL pls_vincular_pessoa_inclusao(nr_seq_inclusao_benef_p, cd_pessoa_fisica_w, nm_usuario_p);

commit;

end;
$body$
