CREATE OR REPLACE PROCEDURE pls_vincular_benef_sib ( nr_seq_segurado_p bigint, nr_seq_retorno_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*	ie_opcao_p
	V = Vincular
	D = Desvincular
*/
BEGIN

if (ie_opcao_p	= 'V') then
	update	pls_retorno_sib
	set	nr_seq_segurado_vinculado	= nr_seq_segurado_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_retorno_p;
elsif (ie_opcao_p	= 'D') then
	update	pls_retorno_sib
	set	nr_seq_segurado_vinculado	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_retorno_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
