CREATE OR REPLACE PROCEDURE pls_vincular_cat_atendimento ( nr_seq_cat_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_atendimento_w		timestamp;
dt_alta_w			timestamp;
nr_seq_segurado_w		bigint;
nr_seq_prestador_w		bigint;
cd_estabelecimento_w		smallint;


BEGIN 
 
update	atendimento_paciente 
set	nr_seq_cat	= nr_seq_cat_p 
where	nr_atendimento	= nr_atendimento_p;
 
/* Obter dados do atendimento */
 
select	dt_entrada, 
	dt_alta, 
	cd_estabelecimento 
into STRICT	dt_atendimento_w, 
	dt_alta_w, 
	cd_estabelecimento_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	coalesce(max(b.nr_sequencia),0) 
into STRICT	nr_seq_prestador_w 
from	pls_prestador		b, 
	estabelecimento		a 
where	a.cd_cgc		= b.cd_cgc 
and	a.cd_estabelecimento	= cd_estabelecimento_w;
 
if (nr_seq_prestador_w	> 0) then 
	/* Obter segurado da CAT */
 
	select	max(nr_seq_segurado) 
	into STRICT	nr_seq_segurado_w 
	from	pls_cat 
	where	nr_sequencia	= nr_seq_cat_p;
 
	insert into pls_prestador_atend_cat(nr_sequencia, nr_seq_prestador, nr_seq_segurado, 
		dt_atendimento, dt_atualizacao, nm_usuario, 
		dt_atualizacao_nrec, nm_usuario_nrec, dt_alta_prevista, 
		dt_alta, nr_atendimento, nr_cat) 
	values (	nextval('pls_prestador_atend_cat_seq'), nr_seq_prestador_w, nr_seq_segurado_w, 
		dt_atendimento_w, clock_timestamp(), nm_usuario_p, 
		clock_timestamp(), nm_usuario_p, null, 
		dt_alta_w, nr_atendimento_p, nr_seq_cat_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
