CREATE OR REPLACE PROCEDURE pls_vincular_cnes_endereco ( cd_pessoa_fisica_p text, ie_tipo_complemento_p bigint, nr_seq_compl_pf_tel_adic_p bigint, cd_cgc_p text, nr_seq_pj_compl_p bigint, nr_seq_ident_cnes_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
cd_cnes_w	varchar(30);


BEGIN 
if (nr_seq_ident_cnes_p IS NOT NULL AND nr_seq_ident_cnes_p::text <> '') then 
	select	cd_cnes 
	into STRICT	cd_cnes_w 
	from	cnes_identificacao a 
	where	a.nr_sequencia	= nr_seq_ident_cnes_p;
 
	/* Mudar para endereço comercial da PF */
 
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_tipo_complemento_p = 2) then 
		update	compl_pessoa_fisica 
		set	nr_seq_ident_cnes	= nr_seq_ident_cnes_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	ie_tipo_complemento	= ie_tipo_complemento_p;
	end if;
	/* Mudar para endereço adicional PF */
 
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_compl_pf_tel_adic_p IS NOT NULL AND nr_seq_compl_pf_tel_adic_p::text <> '') then 
		update	compl_pf_tel_adic 
		set	nr_seq_ident_cnes	= nr_seq_ident_cnes_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	nr_sequencia		= nr_seq_compl_pf_tel_adic_p;
	end if;
	/* Mudar para endereço cartão CNPJ */
 
	if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') and (coalesce(nr_seq_pj_compl_p::text, '') = '') then 
		update	pessoa_juridica 
		set	nr_seq_ident_cnes	= nr_seq_ident_cnes_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp(), 
			cd_cnes			= cd_cnes_w 
		where	cd_cgc			= cd_cgc_p;
	end if;
	/*Mudar para endereço adicional PJ */
 
	if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') and (nr_seq_pj_compl_p IS NOT NULL AND nr_seq_pj_compl_p::text <> '') then 
		update	pessoa_juridica_compl 
		set	nr_seq_ident_cnes	= nr_seq_ident_cnes_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	cd_cgc			= cd_cgc_p 
		and	nr_sequencia		= nr_seq_pj_compl_p;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
