CREATE OR REPLACE PROCEDURE pls_vincular_comu_guia ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_lote_comunicacao_p pls_comunicacao_internacao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_guia_w	pls_guia_plano.cd_guia%type;
dt_agora_w	timestamp	:= clock_timestamp();

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_comunicacao_internacao
	where	nr_seq_lote_inter = nr_seq_lote_comunicacao_p;

BEGIN

select	cd_guia
into STRICT	cd_guia_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

for r_c01 in C01 loop

	update	pls_comunicacao_internacao
	set	nr_seq_guia	= nr_seq_guia_p,
		cd_guia		= cd_guia_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= dt_agora_w
	where	nr_sequencia	= r_c01.nr_sequencia;

end loop;

CALL pls_atualizar_comunic_int_guia(nr_seq_lote_comunicacao_p, nm_usuario_p);

-- o commit já é executado na pls_atualizar_comunic_int_guia
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
