CREATE OR REPLACE PROCEDURE pls_vincular_doc_reembolso_pf ( nr_seq_documento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, doc_vinculado_p INOUT text) AS $body$
DECLARE


nr_seq_novo_doc_pf_w		bigint;
nr_seq_tipo_doc_pf_w		bigint;
cd_pessoa_fisica_w		varchar(30);
ds_arquivo_w			varchar(255);


BEGIN

doc_vinculado_p := 'N';

select	max(a.nr_seq_tipo_documentacao)
into STRICT	nr_seq_tipo_doc_pf_w
from	pls_protocolo_reg_doc	b,
	pls_protocolo_documento	a
where	a.nr_sequencia = b.nr_seq_documento
and	b.nr_sequencia = nr_seq_documento_p;

if (coalesce(nr_seq_tipo_doc_pf_w,0) <> 0) then
	begin

	select	c.cd_pessoa_fisica,
		a.ds_arquivo
	into STRICT	cd_pessoa_fisica_w,
		ds_arquivo_w
	from	pls_segurado		c,
		pls_protocolo_conta	b,
		pls_protocolo_reg_doc	a
	where	a.nr_seq_protocolo = b.nr_sequencia
	and	b.nr_seq_segurado = c.nr_sequencia
	and	a.nr_sequencia = nr_seq_documento_p;

	select	nextval('pls_tipo_documento_pf_seq')
	into STRICT	nr_seq_novo_doc_pf_w
	;

	insert into pls_tipo_documento_pf( nr_sequencia,
					cd_estabelecimento,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_tipo_documento,
					dt_registro,
					nm_usuario_registro,
					cd_pessoa_fisica,
					dt_validade )
				values (	nr_seq_novo_doc_pf_w,
					cd_estabelecimento_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_tipo_doc_pf_w,
					clock_timestamp(),
					nm_usuario_p,
					cd_pessoa_fisica_w,
					null );

	if (length(ds_arquivo_w) > 0) then

		insert into pls_documento_pf(	nr_sequencia,
						cd_pessoa_fisica,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						ds_arquivo,
						nr_seq_tipo_documento,
						nr_seq_tipo_doc_pf )
					values (	nextval('pls_documento_pf_seq'),
						cd_pessoa_fisica_w,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						ds_arquivo_w,
						nr_seq_tipo_doc_pf_w,
						nr_seq_novo_doc_pf_w );

	end if;

	doc_vinculado_p := 'S';

	end;
else
	doc_vinculado_p := 'N';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
