SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_grupo_relac ( nr_seq_contrato_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


ie_tipo_contratacao_w		pls_plano.ie_tipo_contratacao%type;
qt_registros_w			integer;
nr_seq_grupo_contrato_w		bigint;
nr_contrato_w			pls_contrato.nr_contrato%type;


BEGIN

select	max(a.ie_tipo_contratacao)
into STRICT	ie_tipo_contratacao_w
from	pls_contrato		c,
	pls_contrato_plano	b,
	pls_plano		a
where	b.nr_seq_contrato	= c.nr_sequencia
and	b.nr_seq_plano		= a.nr_sequencia
and	c.nr_sequencia		= nr_seq_contrato_p
and	b.ie_situacao		= 'A';

if (ie_tipo_contratacao_w IS NOT NULL AND ie_tipo_contratacao_w::text <> '') then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_regra_contrato_relac
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_situacao		= 'A'
	and	ie_tipo_contratacao	= ie_tipo_contratacao_w;

	if (qt_registros_w > 0) then
		select	max(nr_contrato)
		into STRICT	nr_contrato_w
		from	pls_contrato
		where	nr_sequencia	= nr_seq_contrato_p;

		select	max(nr_seq_grupo_contrato)
		into STRICT	nr_seq_grupo_contrato_w
		from	pls_regra_contrato_relac
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	ie_situacao		= 'A'
		and	ie_tipo_contratacao	= ie_tipo_contratacao_w;

		insert into pls_contrato_grupo(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
				nm_usuario, nm_usuario_nrec, nr_seq_grupo,
				nr_seq_contrato, nr_contrato, cd_estabelecimento,
				ie_reajuste_grupo)
		values (	nextval('pls_contrato_grupo_seq'), clock_timestamp(), clock_timestamp(),
				nm_usuario_p, nm_usuario_p, nr_seq_grupo_contrato_w,
				nr_seq_contrato_p, nr_contrato_w, cd_estabelecimento_p,
				'S');
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
