SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_material_unimed (nr_seq_material_p bigint, ie_tipo_despesa_p text, nr_seq_estrut_mat_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w               bigint;
cd_unidade_medida_w             varchar(10);
ds_material_w                   varchar(2000);
ie_situacao_w                   varchar(245);
cd_material_w                   bigint;
ie_existe_medida_w		bigint;


BEGIN

/*Verifica se existe o material*/

select  count(nr_sequencia)
into STRICT    nr_seq_material_w
from    pls_material
where   nr_seq_material_unimed          = nr_seq_material_p;


if (nr_seq_material_w > 0) then
        CALL wheb_mensagem_pck.exibir_mensagem_abort(265590,'seq_material='||nr_seq_material_p);
		--Mensagem: O material '||nr_seq_material_p||' já existe!
else
        select  max(cd_material) + 1
        into STRICT    cd_material_w
        from    pls_material;

        /*Pega od dados do produto*/

        select 	cd_unidade_medida,
                initcap(nm_material),
                ie_situacao
        into STRICT    cd_unidade_medida_w,
                ds_material_w,
                ie_situacao_w
        from    pls_material_unimed
        where   cd_material     = nr_seq_material_p;

	/*verificar se existe cadastrado oa unidade de medida.*/

	begin

		select	count(*)
		into STRICT	ie_existe_medida_w
		from	unidade_medida a
		where	a.cd_unidade_medida = cd_unidade_medida_w;

	exception
	when others then
		ie_existe_medida_w := 0;
	end;

	if (ie_existe_medida_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265593,'unidade_medida='||cd_unidade_medida_w|| 'seq_material='||nr_seq_material_p);
		--A unidade de medida '||cd_unidade_medida_w||' do produto '||nr_seq_material_p||  não consta cadastrada no sistema!
	end if;

        insert into pls_material(nr_sequencia,
                dt_atualizacao,
                nm_usuario,
                ie_tipo_despesa,
                cd_estabelecimento,
                nr_seq_estrut_mat,
                cd_unidade_medida,
                ds_material,
                ie_situacao,
                nr_seq_material_unimed,
		dt_inclusao)
        values (nextval('pls_material_seq'),
                clock_timestamp(),
                nm_usuario_p,
                ie_tipo_despesa_p,
                cd_estabelecimento_p,
                nr_seq_estrut_mat_p,
                cd_unidade_medida_w,
                ds_material_w,
                ie_situacao_w,
                nr_seq_material_p,
		trunc(clock_timestamp(),'dd'));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
