CREATE OR REPLACE PROCEDURE pls_vincular_material (cd_material_p bigint, ie_tipo_despesa_p text, nr_seq_estrut_mat_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pls_material_w		bigint;
ds_material_w			varchar(255);
cd_unidade_medida_w		varchar(30);
cd_simpro_w			bigint;
qt_conversao_simpro_w		double precision;
cd_tiss_brasindice_w		varchar(20);
qt_conversao_bras_w		double precision;
ds_medicamento_w		varchar(255);


BEGIN

select	ds_material,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,30) cd_unidade_medida_compra
into STRICT	ds_material_w,
	cd_unidade_medida_w
from	material
where	cd_material	= cd_material_p;

select	max(cd_simpro),
	max(qt_conversao)
into STRICT	cd_simpro_w,
	qt_conversao_simpro_w
from	material_simpro
where	cd_material	= cd_material_p;

select	max(substr(tiss_obter_cod_brasindice(cd_apresentacao, cd_laboratorio, cd_medicamento),1,15)),
	max(qt_conversao)
into STRICT	cd_tiss_brasindice_w,
	qt_conversao_bras_w
from	material_brasindice
where	cd_material	= cd_material_p;

select	substr(pls_obter_desc_mat_bras_tiss(cd_tiss_brasindice_w),1,255) ds_medicamento
into STRICT	ds_medicamento_w
;

select	nextval('pls_material_seq')
into STRICT	nr_seq_pls_material_w
;

insert into pls_material(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	ie_tipo_despesa,
	cd_estabelecimento,
	nr_seq_estrut_mat,
	cd_unidade_medida,
	ds_material,
	ie_situacao,
	cd_material,
	cd_simpro,
	qt_conversao_simpro,
	cd_tiss_brasindice,
	qt_conversao,
	dt_inclusao,
	ds_medicamento)
values (nr_seq_pls_material_w,
	clock_timestamp(),
	nm_usuario_p,
	ie_tipo_despesa_p,
	cd_estabelecimento_p,
	nr_seq_estrut_mat_p,
	cd_unidade_medida_w,
	ds_material_w,
	'A',
	cd_material_p,
	cd_simpro_w,
	qt_conversao_simpro_w,
	cd_tiss_brasindice_w,
	qt_conversao_bras_w,
	trunc(clock_timestamp(),'dd'),
	ds_medicamento_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
