CREATE OR REPLACE PROCEDURE pls_vincular_partic_glosa_ocor ( nr_seq_glosa_ocor_p bigint, ie_tipo_p text, nr_seq_participante_p bigint, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE

/*askono - 06/10/2011
	ESTA PROCEDURE TEM A FINALIDADE DE VINCULAR UM DETERMINADO PARTICIPANTE DO PROCEDIMENTO
	PARA A GLOSA/OCORRÊNCIA GERADA.
	É EXECUTADA QUANDO O USUÁRIO UTILIZA O ITEM DE MENU "VINCULAR PARTICIPANTE NA GLOSA/ OCORRENCIA"
*/
/*
	ie_tipo_p = 'G' - Glosa
	ie_tipo_p = 'O' - Ocorrência
*/
BEGIN
--SE FOR GLOSA
if (ie_tipo_p = 'G') then

	update 	pls_conta_glosa
	set	nr_seq_proc_partic = nr_seq_participante_p
	where	nr_sequencia 	   = nr_seq_glosa_ocor_p;

--SE FOR OCORRENCIA
elsif (ie_tipo_p = 'O') then

	update 	pls_ocorrencia_benef
	set	nr_seq_proc_partic = nr_seq_participante_p
	where	nr_sequencia	   = nr_seq_glosa_ocor_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
