CREATE OR REPLACE PROCEDURE pls_vincular_perito ( nr_seq_analise_p bigint, nm_usuario_p text, nr_seq_perito_p bigint, dt_atendimento_p timestamp) AS $body$
DECLARE


ie_horario_valido_w		varchar(10);


BEGIN

/*aaschlote 24/04/2012 OS- 438903*/

if (nr_seq_perito_p IS NOT NULL AND nr_seq_perito_p::text <> '') then
	ie_horario_valido_w := pls_consistir_horarios_perito(nr_seq_perito_p, dt_atendimento_p, ie_horario_valido_w);

	if (ie_horario_valido_w = 'N') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(117930,'');
	end if;
end if;

update	pls_analise_adesao
set	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	dt_atendimento	= dt_atendimento_p,
	nr_seq_perito	= nr_seq_perito_p
where	nr_sequencia	= nr_seq_analise_p;

commit;

end;
$body$
