CREATE OR REPLACE PROCEDURE pls_vincular_pessoa_simulacao ( nr_seq_simul_indiv_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
qt_pessoas_fisicas_w	bigint;
nr_seq_simulacao_w	bigint;
nm_pessoa_fisica_w	varchar(255);
dt_nascimento_w		timestamp;
qt_idade_w		bigint;


BEGIN 
 
select	count(*) 
into STRICT	qt_pessoas_fisicas_w 
from	pessoa_fisica 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
 
select	max(nr_seq_simulacao) 
into STRICT	nr_seq_simulacao_w 
from	pls_simulpreco_individual 
where	nr_sequencia		= nr_seq_simul_indiv_p;
 
if (qt_pessoas_fisicas_w > 0) then 
	 
	select	nm_pessoa_fisica, 
		dt_nascimento, 
		obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A') 
	into STRICT	nm_pessoa_fisica_w, 
		dt_nascimento_w, 
		qt_idade_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	 
	update	pls_simulpreco_individual 
	set	cd_pessoa_fisica	= cd_pessoa_fisica_p, 
		nm_beneficiario		= nm_pessoa_fisica_w, 
		dt_nascimento		= dt_nascimento_w, 
		qt_idade		= qt_idade_w, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia		= nr_seq_simul_indiv_p;
	 
	/*Recallcular a simulação*/
 
	CALL pls_recalcular_simulacao(nr_seq_simulacao_w,nm_usuario_p);
	/*Alterar o resumo da simulação*/
 
	CALL pls_gerar_resumo_simulacao(nr_seq_simulacao_w,nr_seq_simul_indiv_p,'E','BS','I',cd_estabelecimento_p,nm_usuario_p);
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
