CREATE OR REPLACE PROCEDURE pls_vincular_prest_plant_aut ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_conta_proc_prest_w	bigint;
nr_seq_lote_prest_w		bigint;
nr_seq_proc_plant_w		bigint;
vl_vinculado_w			double precision;
vl_saldo_w			double precision;
					
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.vl_vinculado 
	from	pls_lote_prest_plant a 
	where	a.nr_seq_lote = nr_seq_lote_p;
				
C02 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_conta_proc_plant a 
	where	a.nr_seq_lote_plant = nr_seq_lote_p 
	and	coalesce(a.nr_seq_prestador_novo::text, '') = '' 
	and	coalesce(a.nr_seq_prest_plant_item::text, '') = '';

BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_lote_prest_w, 
		vl_vinculado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	max(a.nr_sequencia) 
		into STRICT	nr_seq_conta_proc_prest_w 
		from	pls_conta_proc_plant a 
		where	coalesce(a.nr_seq_prestador_novo::text, '') = '' 
		and	coalesce(a.nr_seq_prest_plant_item::text, '') = '';		
		 
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_proc_plant_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			select	a.vl_saldo_plantonista 
			into STRICT	vl_saldo_w 
			from	pls_lote_prest_plant a 
			where	a.nr_sequencia = nr_seq_lote_prest_w;
			 
			CALL pls_vinc_desv_prest_plant_proc(nr_seq_lote_prest_w, nr_seq_proc_plant_w, 'VA', nm_usuario_p);
			end;
		end loop;
		close C02;				
		end;
	end loop;
	close C01;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
