SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_prot_lote ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE

/*
Rotina responsável por vincular protocolo em um lote pré-existente, somente poderão ser vinculados protocolo que não estejam vinculados a um lote ou que estejam vinculados a um lote sem análise gerada
e somente poderá ser inseridos em um ote que não tenha análise gerada
*/
qt_contas_analise_w	integer;
qt_prot_a700_w		integer;
qt_protocolo_w		integer;
ds_historico_w		pls_prot_conta_hist.ds_historico%type;


BEGIN


select	count(1)
into STRICT	qt_protocolo_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p
and	ie_situacao in ('T','D');
--rá verificar se o protocolo existe e se relmente está integrado
if (qt_protocolo_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296208); -- Protocolo selecionado não existe, ou não integrado
end if;


select 	count(1)
into STRICT	qt_contas_analise_w
from	pls_conta
where	nr_seq_protocolo = nr_seq_protocolo_p
and	(nr_seq_analise IS NOT NULL AND nr_seq_analise::text <> '');
--Irá verificar se o protocolo não possuí análises ja geradas
if (qt_contas_analise_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296203); -- Protocolo com análises vinculadas, favor consistir!
end if;


select	count(1)
into STRICT	qt_prot_a700_w
from	pls_protocolo_conta
where	nr_seq_lote_conta = nr_seq_lote_p
and	ie_status = '7';
--Irá vincular se não é um lote de a700 neste caso não será possível vincular
if (qt_prot_a700_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296205); -- Não é possível vincular o protocolo em um lote de A700
end if;


select	count(1)
into STRICT	qt_prot_a700_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p
and	ie_status = '7';
--Irá vincular se não é um protocolo de a700 neste caso não será possível vincular
if (qt_prot_a700_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296225); -- Não é possível alterar o lote de protocolos de A700!
end if;

select	count(1)
into STRICT	qt_protocolo_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p
and	(nr_seq_lote_conta IS NOT NULL AND nr_seq_lote_conta::text <> '');
--Irá verificar se o protocolo de conta já está vinculado a um lote de análise
if (qt_protocolo_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(686515); -- Este protocolo de contas médicas já está vinculado a um lote de análise.
end if;

select	count(1)
into STRICT	qt_protocolo_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p
and	ie_tipo_protocolo = 'R';
--rá verificar se o protocolo existe e se relmente está integrado
if (qt_protocolo_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(719079); -- Protocolo selecionado não existe, ou não integrado
end if;

select	count(1)
into STRICT	qt_protocolo_w
from	pls_protocolo_conta
where	nr_seq_lote_conta = nr_seq_lote_p;
--Irá verificar se existe um protocolo no lote, caso não exista pode ser vinculado sem restricao
if (qt_protocolo_w	> 0) then
	select	count(1)
	into STRICT	qt_protocolo_w
	from	pls_protocolo_conta
	where	nr_seq_lote_conta = nr_seq_lote_p
	and	ie_status in ('1','2','5');
	--Irá verificar se existe ao menos um protocolo que ainda não tenha sido finalizado por completo
	if (qt_protocolo_w	= 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(296223); -- Todos os protocolos vinculados ao lote estão encerrados, ou em processo
	end if;
end if;


update	pls_protocolo_conta
set	nr_seq_lote_conta = nr_seq_lote_p
where	nr_sequencia = nr_seq_protocolo_p;

commit;

ds_historico_w := 'Vinculado ao lote '||nr_seq_lote_p;

insert into pls_prot_conta_hist(nr_sequencia,
	ds_historico,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_historico,
	ie_tipo_historico,
	nm_usuario,
	nm_usuario_historico,
	nm_usuario_nrec,
	nr_seq_protocolo)
values (nextval('pls_prot_conta_hist_seq'),
	ds_historico_w,
	clock_timestamp(),
	clock_timestamp(),
	clock_timestamp(),
	'17',
	nm_usuario_p,
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_protocolo_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_vincular_prot_lote ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nm_usuario_p text) FROM PUBLIC;

