SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_regra_reajuste ( nr_seq_reajuste_p bigint, nr_seq_regra_p bigint, ie_cobr_retro_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
dt_periodo_inicial_w		pls_regra_reajuste.dt_periodo_inicial%type;
dt_periodo_final_w		pls_regra_reajuste.dt_periodo_final%type;
ds_oficio_ans_w			pls_regra_reajuste.ds_oficio_ans%type;
nr_protocolo_ans_w		pls_regra_reajuste.nr_protocolo_ans%type;
dt_autorizacao_ans_w		pls_regra_reajuste.dt_autorizacao_ans%type;
ds_observacao_w			pls_regra_reajuste.ds_observacao%type;
tx_reajuste_w			pls_regra_reajuste.tx_reajuste%type;
ds_erro_w			varchar(4000);
dt_reajuste_w			pls_reajuste.dt_reajuste%type;
ds_mes_reajuste_w		varchar(7);
dt_referencia_mensalidade_w	pls_reajuste_cobr_retro.dt_referencia_mensalidade%type;
dt_referencia_reajuste_w	pls_reajuste_cobr_retro.dt_referencia_reajuste%type;
ie_indice_reajuste_contrato_w	pls_regra_reajuste.ie_indice_reajuste_contrato%type;
vl_cotacao_w			cotacao_moeda.vl_cotacao%type;

C01 CURSOR FOR
	SELECT	a.ds_mes_ref_mensalidade,
		a.ds_mes_ref_retroativo,
		coalesce(c.qt_parcelas,1) qt_parcelas
	from	pls_regra_reaj_cobr_retro a,
		pls_regra_reajuste_retro b,
		pls_regra_reajuste c
	where	a.nr_seq_regra_retro	= b.nr_sequencia
	and	c.nr_sequencia		= b.nr_seq_regra
	and	c.nr_sequencia		= nr_seq_regra_p
	and	b.mes_aniversario_contrato = ds_mes_reajuste_w
	order by b.mes_aniversario_contrato;

BEGIN

select	dt_reajuste
into STRICT	dt_reajuste_w
from	pls_reajuste
where	nr_sequencia	= nr_seq_reajuste_p;

ds_mes_reajuste_w	:= to_char(dt_reajuste_w,'mm/yyyy');

select	cd_estabelecimento,
	dt_periodo_inicial,
	dt_periodo_final,
	ds_oficio_ans,
	nr_protocolo_ans,
	dt_autorizacao_ans,
	ds_observacao,
	tx_reajuste,
	ie_indice_reajuste_contrato
into STRICT	cd_estabelecimento_w,
	dt_periodo_inicial_w,
	dt_periodo_final_w,
	ds_oficio_ans_w,
	nr_protocolo_ans_w,
	dt_autorizacao_ans_w,
	ds_observacao_w,
	tx_reajuste_w,
	ie_indice_reajuste_contrato_w
from	pls_regra_reajuste
where	nr_sequencia	= nr_seq_regra_p;

if (dt_reajuste_w between dt_periodo_inicial_w and dt_periodo_final_w) then
	if (ie_indice_reajuste_contrato_w IS NOT NULL AND ie_indice_reajuste_contrato_w::text <> '') then
		begin
		select	max(b.vl_cotacao)
		into STRICT	vl_cotacao_w
		from	moeda		a,
			cotacao_moeda	b
		where	a.cd_moeda	= ie_indice_reajuste_contrato_w
		and	a.cd_moeda	= b.cd_moeda
		and	trunc(b.dt_cotacao, 'month')	= trunc(dt_reajuste_w, 'month');
		exception
		when others then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(213712);
		end;
		
		tx_reajuste_w	:= vl_cotacao_w;
	end if;
	
	update	pls_reajuste
	set	cd_estabelecimento	= cd_estabelecimento_w,
		dt_periodo_inicial	= dt_periodo_inicial_w,
		dt_periodo_final	= dt_periodo_final_w,
		ds_oficio_ans		= ds_oficio_ans_w,
		nr_protocolo_ans	= nr_protocolo_ans_w,
		dt_autorizacao_ans	= dt_autorizacao_ans_w,
		ds_observacao		= ds_observacao_w,
		nr_seq_regra		= nr_seq_regra_p,
		tx_reajuste		= tx_reajuste_w,
		ie_indice_reajuste_contrato = coalesce(ie_indice_reajuste_contrato_w,ie_indice_reajuste_contrato),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_reajuste_p;
	
	if (ie_cobr_retro_p = 'S') then
		for c01_w in C01 loop
			begin
			dt_referencia_mensalidade_w	:= to_date(c01_w.ds_mes_ref_mensalidade,'mm/yyyy');
			dt_referencia_reajuste_w	:= to_date(c01_w.ds_mes_ref_retroativo,'mm/yyyy');
			
			insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					dt_referencia_mensalidade, dt_referencia_reajuste, qt_parcelas)
				values (	nextval('pls_reajuste_cobr_retro_seq'), nr_seq_reajuste_p, cd_estabelecimento_w,
					clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
					dt_referencia_mensalidade_w, dt_referencia_reajuste_w, c01_w.qt_parcelas);
			end;
		end loop;
	end if;
else
	ds_erro_w	:= wheb_mensagem_pck.get_texto(280127, 'DT_AJUSTE_P=' || to_char(dt_reajuste_w,'dd/mm/yyyy'));
end if;

ds_erro_p	:= ds_erro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_vincular_regra_reajuste ( nr_seq_reajuste_p bigint, nr_seq_regra_p bigint, ie_cobr_retro_p text, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

