CREATE OR REPLACE PROCEDURE pls_vincular_ressarc_sus_peona (vl_ressarcimento_sus_p bigint, nr_seq_vl_peona_p bigint, ie_opcao_p text, nm_usuario_p text, dt_ressarcimento_sus_p timestamp) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Vincula o valor de ressarcimento SUS à PEONA.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_total_despesa_w	double precision;
nr_seq_peona_w		bigint;


BEGIN
if (nr_seq_vl_peona_p IS NOT NULL AND nr_seq_vl_peona_p::text <> '') then
	if (ie_opcao_p = 'V') then
		update	pls_valores_peona
		set	vl_ressarcimento_sus 	= vl_ressarcimento_sus_p,
			vl_despesa		= vl_despesa + vl_ressarcimento_sus_p,
			dt_ressarcimento_sus	= dt_ressarcimento_sus_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_vl_peona_p;
	elsif (ie_opcao_p = 'D') then
		update	pls_valores_peona
		set	vl_despesa		= vl_despesa - vl_ressarcimento_sus,
			vl_ressarcimento_sus 	 = NULL,
			dt_ressarcimento_sus	 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_vl_peona_p;
	end if;

	select	a.nr_seq_peona
	into STRICT	nr_seq_peona_w
	from	pls_valores_peona a
	where	a.nr_sequencia = nr_seq_vl_peona_p;

	select	sum(a.vl_despesa)
	into STRICT	vl_total_despesa_w
	from	pls_valores_peona a
	where	a.nr_seq_peona = nr_seq_peona_w;

	update	pls_peona
	set	vl_total_despesa	= vl_total_despesa_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia 		= nr_seq_peona_w;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
