CREATE OR REPLACE PROCEDURE pls_vincular_segurado_ans ( nr_seq_tasy_ans_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


cd_usuario_plano_w	varchar(30);
dt_contratacao_w	timestamp;
ie_tipo_registro_w	smallint;
cd_plano_w		varchar(20);
cd_usuario_plano_ant_w	varchar(30);
nr_seq_segurado_w	bigint;


BEGIN

if (ie_opcao_p	= 'C') then
	select	a.nr_sequencia,
		b.cd_usuario_plano,
		a.dt_contratacao,
		CASE WHEN coalesce(dt_rescisao::text, '') = '' THEN 1  ELSE 3 END ,
		c.cd_plano
	into STRICT	nr_seq_segurado_w,
		cd_usuario_plano_w,
		dt_contratacao_w,
		ie_tipo_registro_w,
		cd_plano_w
	from	pls_segurado a,
		pls_segurado_carteira b,
		pls_plano c
	where	a.nr_sequencia	= b.nr_seq_segurado
	and	a.nr_seq_plano	= c.nr_sequencia
	and	a.nr_sequencia	= nr_seq_segurado_p;

	update	pls_sib_tasy_ans
	set	ie_tasy_ans	= 'S',
		ie_situacao_benef	= ie_tipo_registro_w,
		dt_contratacao	= dt_contratacao_w,
		cd_plano	= cd_plano_w,
		cd_usuario_plano	= cd_usuario_plano_w,
		nr_seq_segurado	= nr_seq_segurado_p,
		ie_enviar_ans	= 'A',
		ie_status	= 'V'
	where	nr_sequencia	= nr_seq_tasy_ans_p;

else
	delete	from pls_retorno_consistencia
	where	nr_seq_tasy_ans = nr_seq_tasy_ans_p;

	update	pls_sib_tasy_ans
	set	ie_tasy_ans		= 'N',
		ie_situacao_benef	 = NULL,
		dt_contratacao		 = NULL,
		cd_plano		 = NULL,
		cd_usuario_plano	 = NULL,
		nr_seq_segurado		 = NULL,
		ie_status		= 'I'
	where	nr_sequencia		= nr_seq_tasy_ans_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
