CREATE OR REPLACE PROCEDURE pls_vincular_segurado_solic2 ( nr_seq_solicitacao_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
nr_seq_segurado_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_segurado_w
from	pls_segurado
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_rescisao::text, '') = ''
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_w;

update	pls_solicitacao_comercial
set	nr_seq_segurado	= nr_seq_segurado_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	cd_pf_vinculado	= cd_pessoa_fisica_w
where	nr_sequencia	= nr_seq_solicitacao_p;

commit;

end;
