CREATE OR REPLACE PROCEDURE pls_vincular_senha_lead ( nr_seq_lead_p bigint, nr_seq_senha_lead_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nm_pessoa_fisica_w	pls_solicitacao_comercial.nm_pessoa_fisica%type;
cd_pf_vinculado_w		pls_solicitacao_comercial.cd_pf_vinculado%type;


BEGIN 
 
if (coalesce(nr_seq_senha_lead_p,0) <> 0) then 
	update	pls_solicitacao_comercial 
	set	nr_seq_pac_senha_fila	= nr_seq_senha_lead_p 
	where	nr_sequencia		= nr_seq_lead_p;
	 
	select	nm_pessoa_fisica, 
		cd_pf_vinculado 
	into STRICT	nm_pessoa_fisica_w, 
		cd_pf_vinculado_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_lead_p;
	 
	update	paciente_senha_fila 
	set	dt_vinculacao_senha 	= clock_timestamp(), 
		nm_usuario		= nm_usuario_p, 
		nm_paciente		= CASE WHEN cd_pf_vinculado_w = NULL THEN nm_pessoa_fisica_w WHEN cd_pf_vinculado_w=substr(obter_nome_pf(cd_pf_vinculado_w),1,255) THEN null END , 
		cd_pessoa_fisica 		= cd_pf_vinculado_w 
	where	nr_sequencia 		= nr_seq_senha_lead_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
