CREATE OR REPLACE PROCEDURE pls_vincular_solic_portabilid (nr_seq_portabilidade_p pls_portab_pessoa.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/*
ie
V	Vincular
D	Desvincular
*/
ds_historico_w	varchar(255);
			

BEGIN

update	pls_segurado
set	nr_seq_portabilidade	= CASE WHEN ie_opcao_p='V' THEN  nr_seq_portabilidade_p  ELSE null END ,
	dt_atualizacao 		= clock_timestamp(),
	nm_usuario 		= nm_usuario_p
where	nr_sequencia 		= nr_seq_segurado_p;

if (ie_opcao_p = 'V') then
	ds_historico_w	:= wheb_mensagem_pck.get_texto(1089162, 'NR_SEQ_PORTABILIDADE='||nr_seq_portabilidade_p); --Portabilidade #@NR_SEQ_PORTABILIDADE#@ vinculada no beneficiário
elsif (ie_opcao_p = 'D') then
	ds_historico_w	:= wheb_mensagem_pck.get_texto(1089163, 'NR_SEQ_PORTABILIDADE='||nr_seq_portabilidade_p); --Portabilidade #@NR_SEQ_PORTABILIDADE#@ desvinculada do beneficiário
end if;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '109', clock_timestamp(),
				ds_historico_w, 'pls_vincular_solic_portabilid', null, 
				null, null, null,
				clock_timestamp(), null, null, 
				null, null, null, 
				null, nm_usuario_p, 'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
