SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_tabela_contrato ( nr_seq_tabela_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_contrato_w	bigint;
nm_segurado_w		varchar(60);
ie_tipo_estipulante_w	varchar(2);
nr_seq_plano_w		bigint;
ie_tipo_contratacao_w	varchar(2);
cont_w			integer;


BEGIN 
 
if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') and (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then 
	select	count(1) 
	into STRICT	cont_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
	 
	if (cont_w = 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 296111, null );
	end if;
	 
	select	max(nr_contrato), 
		max(nr_seq_plano) 
	into STRICT	nr_seq_contrato_w, 
		nr_seq_plano_w 
	from	pls_tabela_preco 
	where	nr_sequencia	= nr_seq_tabela_p;
	 
	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 244797, null );
	end if;
	 
	select	max(nr_seq_contrato) 
	into STRICT	nr_seq_contrato_w 
	from	pls_contrato_plano 
	where	nr_seq_tabela	= nr_seq_tabela_p;
	 
	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 244798, 'NR_SEQ_CONTRATO='||nr_seq_contrato_w );
	end if;
	 
	select	max(nr_seq_contrato), 
		max(substr(obter_nome_pf(cd_pessoa_fisica),1,60)) 
	into STRICT	nr_seq_contrato_w, 
		nm_segurado_w 
	from	pls_segurado 
	where	nr_seq_tabela	= nr_seq_tabela_p 
	and	nr_seq_contrato	<> nr_seq_contrato_p;
	 
	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 244799, 'NM_SEGURADO='|| nm_segurado_w ||';'||'NR_SEQ_CONTRATO='|| nr_seq_contrato_w );
	end if;
	 
	select	max(CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'J'  ELSE 'F' END ) 
	into STRICT	ie_tipo_estipulante_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
	 
	/* Ana Oberziner (14/06/2013) - Pode ocorrer a situação de um beneficiário possuir uma liminar judicial para possuir outra tabela de preço, sendo assim, é necessário a vinculação de mais tabelas num contrato. 
	if	(ie_tipo_estipulante_w = 'F') then 
		'O contrato selecionado é individual/familiar não sendo permitido este tipo de vinculação!' 
	else*/
 
	 
	if (ie_tipo_estipulante_w = 'J') then 
		select	ie_tipo_contratacao 
		into STRICT	ie_tipo_contratacao_w 
		from	pls_plano 
		where	nr_sequencia	= nr_seq_plano_w;
		 
		if (ie_tipo_contratacao_w = 'I') then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort( 244800, null );
		end if;
	end if;
	 
	select	count(1) 
	into STRICT	cont_w 
	from	pls_contrato_plano 
	where	nr_seq_contrato	= nr_seq_contrato_p 
	and	nr_seq_plano	= nr_seq_plano_w;
	 
	if (cont_w = 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 244801, null );
	end if;
	 
	update	pls_tabela_preco 
	set	nr_contrato	= nr_seq_contrato_p 
	where	nr_sequencia	= nr_seq_tabela_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_vincular_tabela_contrato ( nr_seq_tabela_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text) FROM PUBLIC;

