CREATE OR REPLACE PROCEDURE pls_vincular_tab_preco_contr ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_tabela_w		bigint;
nr_contrato_w		bigint;
nr_seq_contrato_w	bigint;

C01 CURSOR FOR
	SELECT	c.nr_sequencia,
		a.nr_sequencia
	from	pls_contrato		a,
		pls_contrato_plano	b,
		pls_tabela_preco	c
	where	b.nr_seq_contrato	= a.nr_sequencia
	and	b.nr_seq_tabela		= c.nr_sequencia
	and	(a.cd_cod_anterior IS NOT NULL AND a.cd_cod_anterior::text <> '')
	and	coalesce(c.nr_contrato::text, '') = ''
	and	(c.cd_codigo_ant IS NOT NULL AND c.cd_codigo_ant::text <> '')
	group by c.nr_sequencia,a.nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tabela_w,
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	select	max(nr_contrato)
	into STRICT	nr_contrato_w
	from	pls_tabela_preco
	where	nr_sequencia	= nr_seq_tabela_w;
	exception
	when others then
		nr_contrato_w	:= null;
	end;

	if (coalesce(nr_contrato_w::text, '') = '') then
		update	pls_tabela_preco
		set	nr_contrato	= nr_seq_contrato_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_tabela_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
