CREATE OR REPLACE PROCEDURE pls_vincular_tipo_benef_comer ( nr_seq_benef_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_proposta_w		bigint;
ie_tipo_proposta_w		varchar(10);
dt_inicio_proposta_w		timestamp;
nr_seq_tipo_benef_com_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_benef_comercial	b,
		pls_tipo_benef_comercial	a
	where	b.nr_seq_tipo			= a.nr_sequencia
	and	dt_inicio_proposta_w between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_inicio_proposta_w)
	and	b.ie_tipo_proposta_adesao	= ie_tipo_proposta_w;


BEGIN

select	nr_seq_proposta
into STRICT	nr_seq_proposta_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_benef_proposta_p;

select	ie_tipo_proposta,
	dt_inicio_proposta
into STRICT	ie_tipo_proposta_w,
	dt_inicio_proposta_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_w;

open C01;
loop
fetch C01 into
	nr_seq_tipo_benef_com_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_seq_tipo_benef_com_w IS NOT NULL AND nr_seq_tipo_benef_com_w::text <> '') then
	update	pls_proposta_beneficiario
	set	nr_seq_tipo_comercial	= nr_seq_tipo_benef_com_w,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_benef_proposta_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
