CREATE OR REPLACE PROCEDURE pls_vincular_titulo_receber ( nr_seq_cobranca_p bigint, nm_usuario_p text, cd_complemento_p bigint) AS $body$
DECLARE


nr_seq_empresa_w		bigint;
dt_remessa_retorno_w		timestamp;
nr_titulo_desc_folha_w		bigint;


BEGIN

select	nr_seq_empresa,
	trunc(dt_remessa_retorno,'month')
into STRICT	nr_seq_empresa_w,
	dt_remessa_retorno_w
from	cobranca_escritural
where	nr_sequencia = nr_seq_cobranca_p;

select	max(nr_titulo_desc_folha)
into STRICT	nr_titulo_desc_folha_w
from	cobranca_escritural
where	nr_seq_empresa = nr_seq_empresa_w
and	trunc(dt_remessa_retorno,'month') = dt_remessa_retorno_w;

if (nr_titulo_desc_folha_w > 0) then
	update	cobranca_escritural
	set	nr_titulo_desc_folha 	= nr_titulo_desc_folha_w,
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia = nr_seq_cobranca_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
