SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_vincular_tit_lote_camara ( nr_seq_lote_p bigint, nr_titulo_pagar_p bigint, nr_titulo_receber_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_permite_inclusao_w	varchar(1)	:= 'S';
nr_seq_lote_exist_w	bigint;
nr_titulo_w		bigint;


BEGIN

if (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then
	select	pls_obter_se_tit_rec_camara(a.ie_origem_titulo)
	into STRICT	ie_permite_inclusao_w
	from	titulo_receber	a
	where	a.nr_titulo	= nr_titulo_receber_p;
end if;

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and
	((nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') or (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '' AND ie_permite_inclusao_w = 'S')) then

	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_lote_exist_w
	from	pls_titulo_lote_camara	b,
		pls_lote_camara_comp	a
	where	a.nr_sequencia = b.nr_seq_lote_camara
	and 	((b.nr_titulo_pagar = nr_titulo_pagar_p and coalesce(nr_titulo_receber_p::text, '') = '') or (b.nr_titulo_receber = nr_titulo_receber_p and coalesce(nr_titulo_pagar_p::text, '') = ''));
	--and	a.nr_sequencia <> nr_seq_lote_p;
	exception
	when others then
		nr_seq_lote_exist_w := null;
	end;

	nr_titulo_w := coalesce(nr_titulo_receber_p, nr_titulo_pagar_p);

	if (nr_seq_lote_exist_w IS NOT NULL AND nr_seq_lote_exist_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(271525, 'NR_TITULO=' || nr_titulo_w || ';NR_SEQ_LOTE=' || nr_seq_lote_exist_w);
	else
		insert into pls_titulo_lote_camara(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_lote_camara,
			nr_titulo_receber,
			nr_titulo_pagar,
			ie_tipo_inclusao,
			vl_baixado)
		values (nextval('pls_titulo_lote_camara_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_lote_p,
			nr_titulo_receber_p,
			nr_titulo_pagar_p,
			'M',
			0);
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_vincular_tit_lote_camara ( nr_seq_lote_p bigint, nr_titulo_pagar_p bigint, nr_titulo_receber_p bigint, nm_usuario_p text) FROM PUBLIC;

