CREATE OR REPLACE PROCEDURE pls_vincular_vendedor_prop_onl ( nr_seq_proposta_online_p pls_proposta_online.nr_sequencia%type, nr_seq_vendedor_canal_p pls_proposta_online.nr_seq_vendedor_canal%type, nr_seq_vendedor_p pls_proposta_online.nr_seq_vendedor_pf%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

ds_historico_w	varchar(2000);

BEGIN

update	pls_proposta_online
set	nr_seq_vendedor_canal 	= nr_seq_vendedor_canal_p,
	nr_seq_vendedor_pf 	= nr_seq_vendedor_p
where	nr_sequencia 		= nr_seq_proposta_online_p;

ds_historico_w	:=	'Vendedor vinculado manualmente. '||chr(13)||chr(10)||
			'Canal de venda: '||nr_seq_vendedor_canal_p||' - '||pls_obter_dados_vendedor(nr_seq_vendedor_canal_p,'N');
if (nr_seq_vendedor_p IS NOT NULL AND nr_seq_vendedor_p::text <> '') then
	ds_historico_w := ds_historico_w || chr(13) || chr(10) || 'Vendedor: '||nr_seq_vendedor_p||' - '||pls_obter_dados_vendedor_vinc(nr_seq_vendedor_p,'N');
end if;

CALL hpms_proposal_request_pck.wsuite_generate_hist_proposal(nr_seq_proposta_online_p, null, ds_historico_w, nm_usuario_p, 'N');

commit;

end;
