CREATE OR REPLACE PROCEDURE pls_vincular_vend_pf_solic ( nr_seq_solic_lead_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pf_vinculado_w		varchar(30);
nr_seq_vendedor_vinculado_w	varchar(30);
nr_seq_vendedor_w		bigint;
qt_registros_w			bigint;
nm_usuario_nrec_w		varchar(50);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		a.nr_sequencia
	from	pls_vendedor_vinculado	b,
		pls_vendedor		a
	where	a.nr_sequencia = b.nr_seq_vendedor
	and	(a.cd_cgc IS NOT NULL AND a.cd_cgc::text <> '')
	and	coalesce(ie_auxiliar_pf_vinculado,'N') = 'S'
	and	b.ie_situacao = 'A'
	and	b.cd_pessoa_fisica = cd_pf_vinculado_w;


BEGIN

select	nm_usuario_nrec
into STRICT	nm_usuario_nrec_w
from	pls_solicitacao_comercial
where	nr_sequencia = nr_seq_solic_lead_p;

select	cd_pessoa_fisica
into STRICT	cd_pf_vinculado_w
from	usuario
where	nm_usuario = nm_usuario_nrec_w;

open C01;
loop
fetch C01 into
	nr_seq_vendedor_vinculado_w,
	nr_seq_vendedor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_solic_vendedor_compl
	where	nr_seq_vendedor_canal = nr_seq_vendedor_w
	and	nr_seq_vendedor_vinculado = nr_seq_vendedor_vinculado_w
	and	nr_seq_solicitacao = nr_seq_solic_lead_p;

	if (qt_registros_w = 0) then

		insert into pls_solic_vendedor_compl( nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							cd_estabelecimento,
							nr_seq_vendedor_canal,
							nr_seq_solicitacao,
							dt_inicio_vigencia,
							dt_fim_vigencia,
							nr_seq_cliente,
							nr_seq_vendedor_vinculado )
						values (	nextval('pls_solic_vendedor_compl_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							cd_estabelecimento_p,
							nr_seq_vendedor_w,
							nr_seq_solic_lead_p,
							null,
							null,
							null,
							nr_seq_vendedor_vinculado_w );

	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
