CREATE OR REPLACE PROCEDURE pls_vincula_profissional_prest ( nr_seq_prestador_p bigint, nr_seq_prest_medico_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_prestador_w	bigint;
cd_medico_w		varchar(10);

BEGIN
if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	update	pls_prestador_medico
	set	nr_seq_prestador_ref = nr_seq_prestador_p
	where	nr_sequencia = nr_seq_prest_medico_p;
else
	select  cd_medico
	into STRICT	cd_medico_w
	from	pls_prestador_medico
	where	nr_sequencia = nr_seq_prest_medico_p;

	begin
		select	nr_sequencia
		into STRICT	nr_seq_prestador_w
		from	pls_prestador
		where	cd_pessoa_fisica = cd_medico_w;
	exception
	when others then
		nr_seq_prestador_w := 0;
	end;

	if (nr_seq_prestador_w > 0) then
		update	pls_prestador_medico
		set	nr_seq_prestador_ref = nr_seq_prestador_w
		where	nr_sequencia = nr_seq_prest_medico_p;
	else
		update	pls_prestador_medico
		set	nr_seq_prestador_ref  = NULL
		where	nr_sequencia = nr_seq_prest_medico_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
