CREATE OR REPLACE PROCEDURE pls_vinc_grupo_contrato_princ ( nr_seq_grupo_p bigint, nr_seq_contrato_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
nr_contrato_w			pls_contrato.nr_contrato%type;
nr_seq_contrato_grupo_w		bigint;
ds_erro_w			varchar(255);

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_contrato 
	from	pls_contrato 
	where	((nr_sequencia = nr_seq_contrato_p) or (nr_contrato_principal = nr_seq_contrato_p)) 
	order by nr_contrato;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_contrato_w, 
	nr_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	nextval('pls_contrato_grupo_seq') 
	into STRICT	nr_seq_contrato_grupo_w 
	;
	 
	/*aaschlote 16/05/2011 OS - 300032*/
 
	ds_erro_w := pls_consistir_contrato_grupo(nr_seq_contrato_w, 0, nr_seq_grupo_p, cd_estabelecimento_p, nm_usuario_p, ds_erro_w);
	 
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(262472, 'DS_ERRO=' || ds_erro_w);
		/* Mensagem: DS_ERRO */
 
	end if;
	 
	insert	into	pls_contrato_grupo(	nr_sequencia, nr_seq_grupo, cd_estabelecimento, 
			nr_seq_contrato, nr_contrato, dt_atualizacao, 
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
			ie_reajuste_grupo) 
		values (	nr_seq_contrato_grupo_w, nr_seq_grupo_p, cd_estabelecimento_p, 
			nr_seq_contrato_w, nr_contrato_w, clock_timestamp(), 
			nm_usuario_p, clock_timestamp(), nm_usuario_p, 
			'S');
			 
	CALL pls_atualizar_mes_reaj_pck.alterar_mes_reaj_contr_grupo(nr_seq_contrato_w, null, nm_usuario_p, cd_estabelecimento_p);
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
