CREATE OR REPLACE PROCEDURE pls_vinc_tit_cancel_rec_fat ( nr_seq_lote_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Vincular títulos ao lote de cancelamento de receita das faturas.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_fatura_w		bigint;


BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_fatura_w
	from	pls_fatura a
	where	a.nr_titulo = nr_titulo_p;

	if (coalesce(nr_seq_fatura_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(219738, 'NR_TITULO='||nr_titulo_p);
	end if;

	insert into pls_cancel_rec_fatura(nr_sequencia,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nm_usuario,
		dt_atualizacao,
		nr_seq_lote,
		nr_seq_fatura,
		ie_status)
	values (nextval('pls_cancel_rec_fatura_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_lote_p,
		nr_seq_fatura_w,
		'P');
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
