CREATE OR REPLACE PROCEDURE pls_vinc_tit_cancel_rec_mens ( nr_seq_lote_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
